@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leagueoflegends.com") {

#forum_post_view .forum_post .left .avatar .photo {
    display: none !important;
}
#forum_post_view .forum_post .left .sca_icon {
    display: none !important;
}
#forum_post_view .forum_post .left .avatar  big {
    font-size: 20px !important;
}
.sca_not_agree .post_content, .sca_not_agree .message_footer, .sca_not_agree .avatar_top {
    opacity: 1 !important;
}

}
