css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
/*ASK/FOLLOW "i" LINK*/
.user_menu_info {
  position: relative !important;
  top: -4px !important;
  left: -8px !important;
  -moz-transition: ease-in all .25s !important;
  -webkit-transition: ease-in all .25s !important;
/*  display: none !important;*/
}

/*AVATAR CONTAINER SIZE & POSITION*/
li[id^="post"] .avatar_and_i .post_avatar {
  position: relative !important;
  left: -16px !important;
  width: 72px !important;
  height: 72px !important;
  background-size: 82px 82px !important;
}

/*AVATAR BORDER & BACKGROUND IMAGE ADJUSTMENTS*/
li[id^="post"] .avatar_and_i .post_avatar {
  border: solid 8px rgba(49,71,94, 1) !important;
  border-radius: 16px !important;
  background-color: rgba(49,71,94, 1) !important;
  background-position: -4px -4px !important;
  background-repeat: no-repeat !important;
  background-clip: padding-box !important;
  -moz-transition: ease-in all .35s !important;
  -webkit-transition: ease-in all .35s !important;
}

/*AVATAR BACKGROUND IMAGE HOVER ADJUSTMENTS*/
li[id^="post"] .avatar_and_i .post_avatar:hover {
  border: solid 8px rgba(49,71,94, 1.25) !important;
  border-radius: 16px !important;
  background-position: 0px 0px !important;
  background-repeat: no-repeat !important;
  background-size: 71px 71px !important;
  -moz-transition: ease-in all .25s !important;
  -webkit-transition: ease-in all .25s !important;
}

/*INSIDE BOX SHADOW - NO HOVER*/
li[id^="post"] .avatar_and_i .post_avatar:before {
  position: absolute !important;
  content:"" !important;
  width: 88px !important;
  height: 88px !important;
  top: -3px !important;
  left: -3px !important;
  box-shadow: inset 1px 1px 6px rgba(0,0,0,1.75),
                      inset 1px 1px 9px rgba(0,0,0,1.75),
                      inset 1px 1px 18px rgba(0,0,0,1.75) !important;
  -moz-transition: ease-in all .25s !important;
  -webkit-transition: ease-in all .25s !important;
/*  background-color: rgba(0,0,0,.50) !important;*/
}
.post_avatar:hover:before {
  -moz-transition: ease-in all .25s !important;
  -webkit-transition: ease-in all .25s !important;
  background-color: rgba(255,0,0,0) !important;
}

/*INSIDE BOX SHADOW - HOVER*/
li[id^="post"] .avatar_and_i .post_avatar:hover:after {
  position: absolute !important;
  content:"" !important;
  width: 80px !important;
  height: 80px !important;
  box-shadow: inset 1px 1px 6px rgba(0,0,0,1.75),
                      inset 1px 1px 9px rgba(0,0,0,1.75),
                      inset 1px 1px 18px rgba(0,0,0,1.75) !important;
  background-color: rgba(255,0,0,0) !important;
}}
