css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jin115.com") {
	/* ad block */
	div#container_index,
	div#side_left,
	div#side_right,
	div#header-top-banner,
	iframe#rsslist,
	div.ad_amazon,
	div[class^="google-user-ad"],
	div#top-container p.center a[href^="http://www.amazon.co.jp/exec/"]
	{
		display: none !important;
	}
	img.pict {
		max-width: 900px !important;
	}
	
	/* body */
	body {
		width: 1000px !important;
		min-width: 1000px !important;
	}
	div#top-header,
	div#header,
	div#container,
	div#footer
	{
		width: auto !important;
	}
	
	/* top */
	div#contents_left,
	div#contents
	{
		float: none !important;
		width: 100% !important;
	}
	div#new-tab {
	    padding-left: 130px !important;
	}
	div.index_article_container {
	   width: 970px !important;
	}
	div.autopagerize_page_element div.index_article_container {
	   float: left !important;
	   height: auto !important;
	   width: 474px !important;
	   overflow: auto !important;
	}
	div.autopagerize_page_element div.index_article {
	   width: 468px !important;
	}
	div.autopagerize_page_element div.index_article h2 {
	   height: 80px !important;
	}
	div.autopagerize_page_element div.index_article h2 a {
	   font-size: 16px !important;
	}
	div.autopagerize_page_element div.index_article_header {
	   width: 306px !important;
	}
	div.autopagerize_page_element div.index_article_body {
	   width: auto !important;
	}
	div.autopagerize_page_element div.index_article_body img {
	   height: auto !important;
	   width: 150px !important;
	}
	div.autopagerize_page_element div.index_article_footer {
	   width: 310px !important;
	}
	div.autopagerize_page_element div.index_article_footer_comment {
	   font-size: 14px !important;
	   padding-right: 10px !important;
	   width: auto !important;
	}
	
	/* entry */
	div#top-container {
	   width: auto !important;
	}
	div#top-container div.article_header {
		height: 100px !important;
		background: none !important;
		border: none !important;
	}
	div#top-container div.article_body,
	div#top-container div.article_bodymore,
	div#top-container div.related-articles,
	div#top-container div.article_footer
	{
		margin: 0 auto !important;
	}
	div#top-container ul#tags,
	div#top-container div#category-link,
	div#top-container div#hot_tweet,
	div#top-container div#comment
	{
		margin: 0 auto !important;
		width: 768px !important;
	}
	div#top-container div.article blockquote {
		background-color: #f8f8f8 !important;
		border: none !important;
	}

}
