css:
/* Clean Reddit by Saming */

@-moz-document domain("reddit.com") {
    html {
        width: 1000px; 
        margin: 70px auto; 
        background: url('http://i.imgur.com/ugNXW.png') !important;}
	body {
	   background: transparent !important; 
	   font-family: "Open Sans", "Segoe UI", "Verdana", sans-serif !important; 
	   margin: 50px 0 !important;}
	.side, .footer-parent, .rank {
	   display: none;}
	#header {
	   position: fixed !important; 
	   top: 70px !important;
	   width: 1000px !important;
	   padding-top: 10px !important;
	   background-color: transparent !important;
	   border: none !important;}
	#header:before {
	   content: ""; 
	   display: block; 
	   position: fixed;
	   top: 0px; 
	   left: 0px; 
	   width: 100%; 
	   height: 130px; 
	   border-bottom: 1px solid rgb(95,153,207);
	   background: url('http://i.imgur.com/ty9uy.png');
	   z-index: -1;}
	#header-img {
	   margin-left: 15px; 
	   width: 33px !important;
	   margin-top: -4px !important;
	   z-index: 9;}
	.pagename {
	   position: fixed; 
	   top: -15px; 
	   left: 0px; 
	   display: block; 
	   width: 100%; 
	   text-align: center; 
	   font-size: 80px !important; 
	   height: 80px;
	   opacity: 0.1; 
	   text-transform: lowercase;
	   text-decoration: none !important;}
	#sr-header-area {
	   -moz-transition: top 0.2s;
	   transition: top 0.2s; 
	   position: fixed; 
	   top: 111px; 
	   left: 0px; 
	   display: block; 
	   width: 100%; 
	   text-transform: lowercase; 
	   font-size: 11px !important;
	   z-index: -9; 
	   padding: 5px 0 3px 0 !important;
	   box-shadow: 0 0 10px white, 0 0 5px black;
	   background-color: rgb(250, 250, 250) !important;}
	#sr-header-area:hover {
	   top: 130px !important;}
	span.selected.title {
	   font-size: 13px !important;}
	.tabmenu li a {
	   display: inline-block; 
	   margin-bottom: 4px !important; 
	   border: 1px solid rgb(95,153,207);
	   line-height: 14px;
	   height: 18px;}
	.tabmenu li.selected a {
	   border: 1px solid orangered !important;}
	.tabmenu li a:hover {
	   background-color: white !important;}
	#header-bottom-right {
	   bottom: 4px !important; 
	   background-color: transparent !important;}
	.content {
	   margin-top: 115px !important; 
	   margin-bottom: 100px !important;}
	.title {
	   font-size: 20px !important;}
	.title:visited {
	   color: rgb(200, 200, 250) !important;}
	.nextprev {
	   font-size: 20px !important;
	   text-align: center; 
	   display: block; 
	   bottom: 0px; 
	   left: 0px; 
	   width: 100%; 
	   height: 45px; 
	   font-weight: 600;
	   margin-top: 70px !important;
	   border: 1px solid rgb(95,153,207);
	   background: url('http://i.imgur.com/ty9uy.png');
	   line-height: 40px;}
	.spacer:nth-child(2) {
	   display: none !important;}
	.last-clicked {
	   border: none !important;}
	a[href="/spoiler"] {background-color: black; color: black !important;}
	a[href="/spoiler"]:hover {background-color: transparent; color: #666 !important;}
	.md {max-width: none !important; font-size: 14px !important;}}
