css:
@namespace url(http://www.w3.org/1999/xhtml);

/* ==== GOOGLE - LES BARRES LATÉRALES SONT RÉDUITES ET AFFICHÉES À LA DEMANDE : DÉBUT ==== */
/* Les barres d'options (à gauche des résultats) sont affichées au survol de la souris */
@-moz-document url-prefix(http://www.google),
               url-prefix(https://www.google){
	/* Les barres latérales sont réduites */
   #leftnav{
      -moz-box-sizing: border-box !important;
      position: fixed !important;
      z-index: 999 !important;
      overflow: hidden !important;
      width: 15px !important;
      height: 100% !important;
      top: 0px !important;
	/* En mettant right, les barres seront situées à droite */
      left: 2px !important;
      background: -moz-linear-gradient(rgb(000,000,255) 20%, 
         rgb(255,000,000) 40%, 
         rgb(255,255,000) 60%, 
         rgb(000,128,000) 80%) !important;
   }
   #leftnav *{opacity: 0 !important;}
	/* Les barres latérales sont ouvertes */
   #leftnav:hover{
      overflow: auto !important;
      z-index: 999 !important;
      width: 250px !important;
      height: 100% !important;
      border: Blue 1px solid !important;
      background: White !important;
   }
   #leftnav:hover *{opacity: 1.0 !important;}
}
/* ==== GOOGLE - LES BARRES LATÉRALES SONT RÉDUITES ET AFFICHÉES À LA DEMANDE : FIN ==== */
