css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lwn.net") {

html, body {

padding: 0 !important;
margin: 0 !important;
background-color: darkgreen !important;

}

.Page {

padding: 0 !important;
margin: 0 !important;
border: none !important;
width: 100% !important;
display: block !important;

}

.LeftColumn {

padding: 1em !important;
margin: 0 !important;
border: none !important;
float: left !important;
display: block !important;
background-color: darkgreen !important;
font: 11pt sans-serif !important;
color: white !important;

}

.LeftColumn a {
color: lightgreen !important;
}

.MidColumn {
width: 100% !important;
padding: 1em !important;
margin: 0 !important;
border: none !important;
font: 10pt sans-serif !important;
}

.TextAd, .AdRight {

display: none !important;

}

.SummaryHL {
text-align: center !important;
text-decoration: none !important;
font-size: 95% !important;
}

.GAByline, .FeatureByline, .PullQuote {
border: 1px dotted darkgreen !important;
background-color: lightgreen !important;
font: 10pt sans-serif !important;
float: right !important;
padding: 5px 10px !important;
margin: 0 10px !important;
width: 25% !important;
text-align: center !important;
}

.invisible {
}

.GAByline p {
text-align: center !important;
}

.tlrw {
border: 1px dotted darkgreen !important;
background-color: lightgreen !important;
font: 10pt sans-serif !important;
margin: 0 10px !important;
}

table.TopNavigation {
max-width: 100% !important;
width: 100% !important;
}

td.NavLink {
max-width: 20% !important;
}

div.MidColHeader {
max-width: 100% !important;
width: 100% !important;
}

div.ArticleText {
max-width: 100% !important;
width: 100% !important;
font-size: 11pt !important;
}

pre {
/*width: 80em !important;*/
margin: 0 20px !important;
padding: 5px 25px !important;
background-color: #fafafa !important;
font: 10pt monospace !important;
}

.CommentBody * {
font: 11pt sans-serif !important;
}

.BigQuote {
color: darkgray !important;
padding: 2px !important;
}

.FormattedComment pre {
background-color: white !important;
padding: 0 !important;
margin: 0 !important;
}

.Headline, .Cat2HL {
background-color: transparent !important;
text-align: center !important;
}

.ArticleText > font {
font-size: smaller !important;
display: block !important;
text-align: center !important;
}

.ArticleText span {
color: gray !important;
}

.MidColumn a {
color: darkgreen !important;
}

.MidColumn a:hover {
color: lightgreen !important;
}

.MidColumn a:visited {
color: green !important;
}

body > center > p > font {

padding: 0 !important;
margin: 0 !important;
border: none !important;
display: block !important;
font: 11pt sans-serif !important;
color: white !important;

}

body > center > p > font > a {

color: lightgreen !important;

}

.SideBox a.Current {
background-color: inherit !important;
font-weight: bold !important;
}

.SideBox a.Current:before {
content: "â€¢ " !important;
color: white !important;
}

.Cat3HL {
background-color: inherit !important;
}

}
