css:
@-moz-document url-prefix(http://www.shacknews.com/chatty) {

#footer {
    display: none !important;
}

#container {
    color: #ccc  !important;
    background: #0c1118  !important;
}

#shacktags_legend {
    top: -285px !important;
}

table#shacktags_legend_table {
    display: table  !important;
}

a#shacktags_legend_toggle {
    display: none  !important;
}

#chatty_comments_wrap .jt_quote {
    font-family: Cambria, serif  !important;
}

#chatty_comments_wrap .jt_code, #chatty_comments_wrap .jt_codesmall {
    font-family: Consolas, monospace  !important;
}

.oneline_user {
    color: #ffba00 !important;
    font-weight: normal !important;
}

div.postmeta span.author span.user, div.postmeta span.author span.user a {
    color: #ffba00 !important;
}

#chatty_comments_wrap .this_user {
    color: #66ccff  !important;
}

div#chatty_comments_wrap {
    border-top: 1px solid #252930  !important;
}

.border-grd {
    background: transparent  !important;
    height: 5px  !important;
    margin: 0  !important;
}

div.article-body p {
    font-size: 90%  !important;
    line-height: 16px  !important;
}


span.oneline_body {
    font-size: 14px !important;
}

div.postbody {
    font-size: 14px !important;
}

div.postbody a:link, div.postbody a:visited, div.postbody a:active, div.postbody a:hover {
    color: #bfa55d  !important;
}

div.postbody a:hover {
    color: #e5c050  !important;
}

div.fullpost {
    border-top-color: #718988  !important;
}

div.fpmod_informative {
    border-top: solid 2px #00bff3  !important;
}


div.newcommentform {
    position: relative !important;
    width: inherit !important;
    min-width: 818px !important;
}

div#shacktags_legend {
    margin-top: -5px  !important;
}

p.rules {
    visibility: hidden  !important;
}

#frm_body {
    font-size: 14px  !important;
}

div.olauthor_174581 span.oneline_user,
div.olauthor_171217 span.oneline_user {
    color: #ffba00 !important;
}

div.olauthor_9980 span.oneline_user:after {
    display: none !important;
}
}
