css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("weibo.com") {
/* ads */
.M_activities,
#pl_rightmod_promotion,
#trustPagelete_recom_allinone,
#pl_rightmod_help,
#pl_rightmod_noticeboard,
/* 关注的话题 */
/* weibo recommened on reply page */
#pl_content_commentTopNav,
/* 感兴趣的人 */
#trustPagelete_recom_interest,
/* micro group*/
.W_other_app,
/* user page, sina vip label */
.W_sina_vip,
/* user page, 他的标签*/
#pl_content_hisTags,
/* user page, 关注他的人同时关注了 */
#pl_relation_recommendAttUsers,
/* user page, 这些人也关注他 */
#pl_content_chainFollowers,
/* user page, 我和他都关注了 */
#pl_content_sameFriends,
/* user page, abuse report */
#pl_content_hisOperationPlate,
/* user page, fans list */
#pl_content_hisFans,
/* user page, hot topie */
.B_one_feed_other #pl_content_promotetopic,
/* user page, badge */
#pl_content_medal,
/* reminder above comments on user's weibo detail page */
.feed_repeat .private,
/* reminder above comments */
dd.content .private,
/* skin switcher */
.templete_enter,
/* go back to top */
#base_scrollToTop,
/* notify */
#pl_content_versionTip,
/* footer */
.layer_menu_list,
.global_footer,
/* comment avator */
.comment_list dt,
/* avators in weibo */
.feed_list .face,
/* */
.i_conn_public,
/* 日期 */
#pl_content_mood,
/* 主菜单右侧菜单项 */
.right .person > li:nth-child(n+3),
/* 顶部菜单 */
.global_header .header .list,
/* 广告 */
.deal_view2,
/* 右侧菜单 */
#pl_nav_outlookBar,
/* 游戏推荐 */
#pl_common_fun,
/* 可能感兴趣的人 */
#pl_content_homeInterest,
/* 人气用户推荐 */
#pl_relation_recommendPopularUsers,
/* 可能感兴趣的微群 */
#pl_content_interestgroup,
/* 可能感兴趣的活动 */
#pl_content_allInOne,
/* 关注的话题 */
#pl_content_topic,
/* 公告栏 */
#pl_common_noticeboard,
/* 怎样玩转新浪微博？ */
#pl_common_help,
/* 新浪微博意见反馈 */
#pl_common_feedback,
/* 发微博下面的选择框 */
.newFilter,
/* 发微博右上方的推荐话题 */
#pl_content_publisherTop .key,
/* 发微博左上方的背景图片 */
#pl_content_publisherTop .title
{
    display:none!important;
}

/* hide avator, fill left margin */
.feed_list .content {
    font-size: 15px!important;
    margin-left: 0!important;
}

/* hide comment avator, fill left margin */
.comment_list dd {
    margin-left: 0!important;
}
/* hide comment avator */

/* 右侧边栏模块的底边 */
.W_rightModule {
    border-bottom: 0!important;
}

/* 隐藏发微博下面的选择框后，隐藏留下的多余空隙 */
.feed_lists {
    margin-top:0!important;
}

.feed_list .comment {
    font-size: 13px!important;
}

/* send comment */
.feed_list .repeat textarea {
    width: 98%!important;
}

/* 装饰用小图标 */
.send_weibo .kind a,
.send_weibo .kind .W_moredown {
    background-image: none!important;
}

/* char counter */
.num > span {
    position: absolute;
    top: -30px;
    right: 0;
}
}

/* adjust top search input */
.W_miniblog #pl_content_top .global_header .header .logo {
    float: left;
    position: relative!important;
}
