css:
@namespace url(http://www.w3.org/1999/xhtml);

/* ==== YAHOO! - LES BARRES LATÉRALES SONT RÉDUITES ET AFFICHÉES À LA DEMANDE : DÉBUT ==== */
/* Les barres d'options (à gauche des résultats) sont affichées au survol de la souris */
@-moz-document domain(search.yahoo.com){
	/* Les barres latérales sont réduites */
   #sidebar,
   .lr-c,
   .lp{
      -moz-box-sizing: border-box !important;
      position: fixed !important;
      z-index: 99 !important;
      overflow: hidden !important;
      width: 15px !important;
      height: 100% !important;
      top: 100px !important;
	/* En mettant right, les barres seront situées à droite */
      left: 2px !important;
      background: -moz-linear-gradient(rgb(128,000,128) 20%, 
         rgb(148,000,211) 40%,
         rgb(255,255,000) 60%,
         rgb(255,215,000) 80%) !important;
   }
   #sidebar *,
   .lr-c *,
   .lp *{opacity: 0 !important;}
	/* Les barres latérales sont ouvertes */
   #sidebar:hover,
   .lr-c:hover,
   .lp:hover{
      overflow: auto !important;
      z-index: 99 !important;
      width: 250px !important;
      height: 100% !important;
      padding-left: 15px !important;
      border: Purple 1px solid !important;
      background: White !important;
   }
   #sidebar:hover *,
   .lr-c:hover *,
   .lp:hover *{
      opacity: 1.0 !important;
   }
   .fcts_hover{z-index: 999 !important;}
}
/* ==== YAHOO! – LES BARRES LATÉRALES SONT RÉDUITES ET AFFICHÉES À LA DEMANDE : FIN ==== */
