css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gierex.eev.pl") {

body { background: url('http://dl.dropbox.com/u/7788634/junk4/Wallpaper-scenery-landscape-open-space-meadow-trees-grass-normal.jpg') fixed no-repeat center #333 !important; color: #fff !important; }

.bodyline {
    background: url('http://dl.dropbox.com/u/7788634/junk4/Wallpaper-scenery-landscape-open-space-meadow-trees-grass-blurred.jpg') fixed no-repeat center #222 !important;
    padding: 10px 25px !important;
    border: 2px solid #222 !important;
    box-shadow: 0 0 4px #000 !important;
    border-radius: 6px 6px 6px 6px !important;
}

.table0 { background: url('http://dl.dropbox.com/u/7788634/junk4/Wallpaper-scenery-landscape-open-space-meadow-trees-grass-blurred.jpg') fixed no-repeat center #222 !important; border: none !important; }

table[background*="bot.gif"] { display: none !important; }
a[href*="glitter-graphics"] { display: none !important; }

a { color: #4f4 !important; }
a:hover { color: #fff !important; text-decoration: none !important; }
.gen, .genmed, .gensmall, p, td, .postdetails, .pagination { color: #fff !important; }

.pagination a, .pagination a:visited, .pagination a:active { border: 1px solid #000 !important; }
.forumline { background-color: transparent !important; background: url('http://dl.dropbox.com/u/7788634/junk4/tlo.png') repeat scroll 0 0 transparent !important; border: 1px solid #000 !important; border-radius: 5px 5px 5px 5px; }
th, td.rowpic, td.cat, td.catHead, td.catBottom { background: url('https://dl.dropbox.com/u/7788634/junk4/w1h1r255g255b255a38%20.png') repeat scroll 0 0 transparent !important; }
td.row1, .pagination a, .pagination a:visited, .pagination a:active { background: url('https://dl.dropbox.com/u/7788634/junk4/w1h1r255g255b255a20%20.png') repeat scroll 0 0 transparent !important; }
td.row2, .code, td.quote, td[background*="fon_top01.gif"] { background: url('https://dl.dropbox.com/u/7788634/junk4/w1h1r255g255b255a26%20.png') repeat scroll 0 0 transparent !important; }
td.row3, td.row3Right, td.spaceRow { background: url('https://dl.dropbox.com/u/7788634/junk4/w1h1r255g255b255a31%20.png') repeat scroll 0 0 transparent !important; }
[src*="kenexspamcenterrobotlog.jpg"] { width: 0 !important; height: 0 !important; padding-left: 323px !important; padding-top: 129px !important; background: url('https://dl.dropbox.com/u/7788634/junk4/KSCLOGO.png') repeat scroll 0 0 transparent !important; }

}
