*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.gentoo.org") {
    html, body {
        background-color: #000000 ! important; 
        background-image: url('data:image/png;base64,') ! important; 
/*        background-attachment: fixed ! important;*/
        background-repeat: repeat-x ! important; 
        color: #ffffff ! important;
}

    p, h1, h2, h3, h4 {
        background-color:transparent ! important; 
        color:#CCCCCC ! important;
    }
    th {
        background-color: #151026 ! important; 
        background-image: none ! important;
        color: #cccccc ! important;
    }
    A:link, a, a.ab, .q {
        color: #00ff00 ! important; 
        text-decoration: none ! important;
    }
    A:visited {
        color: #aaaa88 ! important;
    }
    A:active {
        color:red ! important; 
        font-style: italic ! important;
    }
    A:hover {
        color: #00ff00 ! important;
        text-decoration: underline ! important;
    }

    /* Strip off */
    .menu {
        background: none!important;
        margin: 0!important;
        padding: 0!important;
    }

    /* Main horizontal menu */
    table > tbody > tr > td[width="100%"] > p.menu > a.menulink {
        color: #00ff00 ! important;
        display: block;
        padding: 8px;
    }
    table > tbody > tr > td[width="100%"] > p.menu {
        background: #65538b!important;
        margin: 15px 1% 0 1%!important;
        padding: 0!important;
        color: #ffffdd!important;
        border-radius: 7px;
    }
    table > tbody > tr > td[width="100%"] > p.menu > a.menulink {
        color: #ffffdd!important;
        display: inline-block;
    }
    table > tbody > tr > td[width="100%"] > p.menu > a.menulink:hover {
        /*background: #151026!important;*/
        color: #00ff00!important;
        text-decoration: none!important;
    }

    /* Search widget */
    html body form#form1 table tbody tr td table tbody tr td table td {
        display: inline-block;
    }
