css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://browser/content/tabview.html"){
#bg{display:none !important;}

#content{color: #000 !important;background-color: #000000 !important;
background-image: -moz-linear-gradient(top,rgba(255,255,255,.2), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 80%, rgba(255,255,255,.2)), -moz-linear-gradient(left,rgba(255,255,255,.2), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 80%, rgba(255,255,255,.2)),
url("chrome://mozapps/skin/extensions/background-texture.png") !important;}

.groupItem {background-color:rgba(128,128,128,.5)!important;}
.tab, #actions {background-color: #D7D7D7 !important;}
.tab.focus {background-color: #000000 !important;}
.tab.focus {box-shadow: 0 1px 3px 2px rgba(0,0,0,.60)!important;}
.tab-title {text-shadow: none !important; color: white !important;}
input.name {text-shadow: -1px 1px 1px black !important; font-weight: bold !important; color: white !important;}
input.name:focus {color: #D7D7D7 !important;}
#exit-button,.favicon {background-color: inherit !important;}
#searchbox {background-color:rgba(128,128,128,.5)!important; color: #D7D7D7 !important;}

}
