css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {

html,
body,
#playerContainerWrapper,
#outline
{
    background: #141414 !important;
}


#videoDetailInformation {
    border: 3px solid #ccc !important;
    background: #FBFBFF !important;
    padding: 0 8px 2px !important;
}
#topVideoInfo .ch_prof, #topVideoInfo .userProfile {
    width: 280px !important;
}
.ch_breadcrumb>#breadcrumb a,
.textContainer {
    color: #DDD !important;
}
.nicoliveNotification {
    color: #fff;
    background: #333;
    border: 1px solid #ddd;
}
.nicoliveNotification .centerColumn .time {
    color: #ddd !important;
}
.nicoliveNotification .centerColumn .title .nicoliveLink {
    color: #fff !important;
}



#videoHeaderDetail h2 {
max-width: 978px !important;
}
body.size_normal {
min-width: 978px !important;
}
#videoHeader, 
#videoHeaderDetail,
#videoTagContainer.default {
width: 978px !important;
min-width: 898px !important;
}
#videoMenuWrapper,
#videoMenuTopList {
width: 978px !important;
}
#videoTagContainer {
padding-right: 0px !important;
}
#videoTagContainer .tagInner {
height: 90px !important;
}
#videoTagContainer.active .tagInner {
height: auto !important;
overflow: visible !important;
}
#videoTagContainer .tagInner #videoHeaderTagList li a, #videoHeaderDetail h2 {
    color: #EEE !important;
}
#videoTagContainer .tagInner #videoHeaderTagList .toggleTagEdit .toggleTagEditInner .videoHeaderTagEditLink {
    color: #747 !important;
}
#videoTagContainer .tagInner #tagEditContainer p {
    color: #999 !important;
}

#videoHeaderTagList .videoHeaderTag {
height: 14px !important;
font-size: 0.75rem !important;
background: #262626 !important;
border-radius: 9px !important;
margin: 0 32px 5px 0 !important;
padding: 1px 9px 5px !important;
-webkit-box-shadow: inset 0 0 5px rgba(240,240,240,0.2);
}
#videoHeaderTagList .videoHeaderTag:hover {
background: #777 !important;
}
#videoHeaderTagList .videoHeaderTag>* {
text-decoration: none !important;
}
#videoTagContainer .tagInner #videoHeaderTagList li .tagControlContainer .dic span,
#videoTagContainer.isEdit .tagInner #videoHeaderTagList .videoHeaderTag.isNoOwnerLock2.isNoVideoOwner.isLock.isCategory .videoHeaderTagCategory,
#videoTagContainer.isEdit .tagInner #videoHeaderTagList .videoHeaderTag.isNoOwnerLock2.isNoVideoOwner.isLock .videoHeaderTagLock,
#videoTagContainer.isEdit .tagInner #videoHeaderTagList .videoHeaderTag.isNoOwnerLock2.isNoVideoOwner .videoHeaderTagDelete {
width: 20px !important;
height: 20px !important;
vertical-align: middle !important;
}
#videoTagContainer .tagInner #videoHeaderTagList li .tagControlContainer,
#videoTagContainer .tagInner #videoHeaderTagList li .tagControlEditContainer {
padding: 3px 3px !important;
}
#videoTagContainer .tagInner #videoHeaderTagList .toggleTagEdit {
background-color: #F2F2F2 !important;
height: 65px !important;
padding: 4px 4px 4px;
border-radius: 7px;
}
#videoTagContainer .tagInner #videoHeaderTagList .toggleTagEdit .toggleTagEditInner {
margin-bottom:3px;
}


.videoDetailOpenButton, .videoDetailCloseButton,
li.videoMenuList a, li.videoMenuListNicoru a {
    color: #EEE !important;
}
.channel .ch_prof {
background: #E7E7E7 !important;
border: 2px solid #CCC !important;
width: 330px !important;
}
.channel .ch_prof h6,
.channel .ch_prof p.bread {
font-size: 100% !important;
font-weight: bold !important;
}
.channel .ch_prof h6 a {
font-weight: bold !important;
}


#foot_inner {
padding: 6px 0 28px !important;
}

#playerAlignmentArea.size_normal {
width: 898px;
}
#playerAlignmentArea.size_normal #playerContainer.controll_panel #playerTabWrapper {
display: none;
}
#playerAlignmentArea.size_normal #playerContainer.appli_panel #playerTabContainer {
bottom: 0px !important;
}

#playerAlignmentArea.size_medium #playerContainer.controll_panel #playerTabWrapper {
height: 461px !important;
}
#playerAlignmentArea.size_medium #playerContainer.controll_panel #playerTabWrapper>#playerTabContainer>.playerTabContent { 
height: 411px !important; 
}
#playerAlignmentArea.size_medium #playerContainer.appli_panel #playerTabContainer {
bottom: 0px !important;
}

.player-panel-tabs .player-tab-content {
top: 43px !important;
}
.panel-body {
top: 30px !important;
}
.player-tab-content {
bottom: 5px !important;
padding: 0 5px !important;
}
div.panel-grid div.slick-viewport {
height: 357px !important;
}
div.ng-grid div.slick-viewport {
height: 347px !important;
}

      

#playerContainer,
#playerContainerSlideArea
{
margin-left: auto !important;
margin-right: auto !important;
}
      
#outline.wrapper {
padding: 0 0 0px !important;
color: #EEE !important;
}
.outer {
margin: 0 auto 0px !important;
}
#videoInfo .videoMainInfoContainer {
padding-bottom: 0px !important;
margin-bottom: 0px !important;
border-bottom: #bbb solid 0px !important;
}
#videoComment {
width: 750px !important;
}
#videoComment h4,
.infoHeadOuter p span,
div.profile h4 > a,
div.profile p > a {
color: #EEE !important;
}
div.videoDescription > a,
.mylistLinkButton {
color: #E90 !important;
}


#videoHeaderMenu,
#videoDetailInformation,
#playlist,
#textMarquee,
#searchResultExplorer,
#playerCommentPanelOuter,
#ichibaPanel,
#leftPanel,
#appliPanel,
#searchResultExplorerContentWrapper,
#searchResultExplorerExpand,
#videoExplorerExpand,
#pageFooter,
#wallImageContainer,
#alertWindow,
#chipWallList,
#feedbackLink,
#outline.wrapper div.outer div.sidebar,
#tagRelatedBanner,
#nicommendContainer,
#nicoIchiba,
#hiddenUserProfile,
#videoInfoHead .videoEditMenu,
#videoInfoHead .videoThumb,
#videoInfoHead .videoInformation,
#videoShareLinks,
#bottomVideoDetailInformation,
#videoHeader .toggleDetailExpand,
#superBanner,
.parentVideoInfo,
.shortVideoInfo,
.videoMenuToggle.videoMenuToggleButton,
.noNicoru .nicoru-button,
.nicoru-button .nicorub-icon,
.nicoru-button .nicorub-balloon,
.socialButtons,
.appliPanel,
.playerTabAds
{
    display: none !important
}

}
