/*
 * User Styles - Dark Gray Theme
 *
 * Author	 : QuestionMarkFTW
 * Updated	 : April 12, 2010
 * Description	 : Modifies the colours and a few other things on userstyles.org
 * Usage      	 : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */ 

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
    html, body {
	background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAENCAYAAADOoA9GAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABZSURBVHjaYmBgYPjPACP+MYFIEPEflfUflwQRSojgUiyGX4IkJTTRi5P4R39T/tFByT8qa6OJof8GPJgGPjHQI/FTki+pWFCQXlQhsZiBmBFMwLlgwAgQYAAYPe0bQ81rlgAAAABJRU5ErkJggg==") top left repeat-x !important;
	color: #fff !important;
}
    p, h1, h2, h3, h4 {
        background-color:transparent ! important; 
        color:#CCCCCC ! important;}
    A:link, a, a.ab, .q {
        color:#a8a8a8 ! important; 
        text-decoration: none ! important;}
    A:visited {
        color: #d5bf9b ! important;}
    A:active {
        color:red ! important; 
        font-style: italic ! important;}
    A:hover {
        color:#CCCCCC ! important;}
*
{
font-family: TimesNewRoman ! important;
}
    #style-list
    {
        -moz-column-count: 3 !important;
        font-size: small !important;
    }
    #Header
    {
        background: transparent ! important;
        border: 0px ! important;
    }
    #CommentBox
    {
        color: #000000 ! important;
    }
    #Form fieldset legend
    {
        color: #ffffff ! important;
    }
    #Form form
    {
        border-top-color: #555555 ! important;
        border-bottom-color: #555555 ! important;
        background-color: #555555 ! important;
    }
    #Form.StartDiscussion label, #Form.AddComments label
    {
        color: #dddddd ! important;
    }
    #Form li
    {
        color: #dddddd ! important;
    }
    #Form label a
    {
        border-top-color: #555555 ! important;
        border-right-color: #555555 ! important;
        border-bottom-color: #555555 ! important;
        border-left-color: #555555 ! important;
        background-color: #555555 ! important;
    }
    #Panel
    {
    background-image: none ! important;
    }
    #header-bar {
        background: transparent ! important;
        border: 0px ! important;
    }
    #navigation a, #navigation a:visited, #navigation .current-page {
        background: transparent ! important;
        border: 0px ! important;
        color: #cccccc ! important;
    }
    #Comments .CommentBody
    {
        color: #999999 ! important;
    }
    #Session
    {
        color: #dddddd ! important;
    }
    #Header ul li a
    {
        background-color: #000000 ! important;
    }
    #css
    {
    color: #000000 ! important;
    }
    #style_long_description
    {
    color: #000000 ! important;
    }
    #search input[type="submit"]
    {
        border: 0px solid #44a ! important;
        background-color: #000 ! important;
        color: #555555 ! important;
   }
    pre {
        background-color: transparent ! important;
        color: #ccb7b7 ! important;
        border: 1px dashed #cccccc ! important;}
    textarea,input[id="short-description"],input[name="style[before_screenshot]"],input[name="style[after_screenshot]"] {
        background-color: transparent ! important;
        color: #ccb7b7 ! important;
        border: 1px dashed #cccccc;}
    input[id="long-description"] {
        height: 50px ! important;}
    #obsoletion-message {
        background-color: transparent ! important;
        border: 3px solid #3c3c3c ! important;}
    #Discussions li ul
    {
        background-color: #000000 ! important;
        border-bottom-color: #ffffff ! important;
    }
    #simple-link-ads, #wonderful-ad-area, .ad
    {
        display : none !important;
    }
    .NoNewComments, #Discussions li.Read ul
    {
        background-color: #222222 ! important;
    }
    .NewComments, #Discussions li.Read.NewComments ul
    {
        background-color: #000000 ! important;
    }
    .DiscussionType
    {
        color: #ffffff ! important;
    }
    /* replace stars with nicer icon - by dougeeebear */
    img[src*="star_1"]
    {
        width: 0 !important;
        height: 12px !important;
        padding-left: 12px !important;
        margin-left: 1px !important;
        margin-bottom: -1px !important;
        background-image: url(data:image/x-png;base64,) 
!important;
    }
    img[src*="star_05"]
    {
        width: 0 !important;
        height: 12px !important;
        padding-left: 12px !important;
        margin-left: 1px !important;
        margin-bottom: -1px !important;
        background-image: url(data:image/x-png;base64,)
!important;
    }
    img[src*="star_0.png"]
    {
        width: 0 !important;
        height: 12px !important;
        padding-left: 12px !important;
        margin-left: 1px !important;
        margin-bottom: -1px !important;
        background-image: url(data:image/x-png;base64,)!important;}
    .ad{
        display:none!important;
    }
    #logo > a:hover, #Header h1 a:hover {
	border-bottom: none !important;
    }
    #logo > a, #Header h1 a {
	display: block !important;
	width: 268px !important;
	height: 64px !important;
	text-indent: -700em !important;
	background-image: url("data:image/png;base64,") !important;
	}
}
