css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weaselzippers.us") {

/* Global styles */
body {background: #000 !important;color: #FFF !important;}
p {color: #FFF !important;}
h1,h2,h3,h4,h5,h6,li {color: #FFF !important;}

a, a:visited {color: #F65273 !important;}
a:hover {background: #000 !important;
color: #FFE662 !important;
}

/* Image caption style */
.wp-caption {background: #322 !important;
border: 1px dashed #800 !important;
color: #EF1241 !important;}

/* Header link style */
h2 a, h2 a:visited {color: #F65273 !important;}
h2 a:hover {background: #000 !important;
color: #FFE662 !important;
}

/* Input boxes */
input,textarea {-moz-appearance: none !important;
background: #000 !important;
color: #FFF !important;}

/* Entry and blockquote styles */
.entry a, .entry a:visited {color: #F65273 !important;}
.entry a:hover {background: #000 !important;
color: #FFE662 !important;
} blockquote {
background: none repeat scroll 0% #111 !important;
color: #EEE !important;
border: 1px solid #FFF !important;
} .entry blockquote blockquote {
border: none !important;
}

/* Metadata border between entries */
p.postmetadata {border-bottom: 1px dotted #F65273 !important;}

/* ZipWire widget and recent comments */
.widget.widget_categoryposts,.recentcomments {
background: #111 !important;
color: #FFF !important;
}
.widget_categoryposts a, .recentcomments a {color: #FFF !important;}
.widget_categoryposts a:hover, .recentcomments a:hover {
background: #F65273 !important;
color: #000 !important;
}

/* Comment section */
div.comment-body {background: #111 !important;
color: #FFF !important;}
div.comment-body blockquote {border: #111 !important;}
.comment {border: 1px solid #F65273 !important;
background-color: #111111 !important;}

/* Tweets */
.twt-border {box-shadow: none !important;
border: none !important;}
blockquote.twt-tweet {border: 3px double #444 !important;}
.twt-tweet a {color: #EEE !important;}

}
