#contactsOnline .entry {
	border-top: 1px solid #FFFFFF;
	cursor: pointer;
	display: block;
	height: 20px!important;
	padding: 0px 0 4px!important;
	width: 175px!important;
}

#contactsOnline .entry a.picture {
	color: #FFFFFF;
	display: block;
	float: left;
	min-height: 19px!important;
	text-align: right;
	width: 33px!important;
}

#contactsOnline .entry a.picture img {
	border: 1px solid #FFFFFF;
	margin-right: 8px;
	height: 22px!important;
	width: 20px!important;
}

#contactsOnline .entry a.plain {
	color: #333333;
	display: block;
	float: left;
	height: 20px!important;
	overflow: hidden;
	padding-top: 2px;
	width: 95px!important;
}

#contactsOnline .entry a.plain .realname {
	display: none!important;
}

#contactsOnline .entry .status {
	float: right;
	min-height: 18px!important;
	padding-top: 3px!important;
	width: 20px!important;
}

