css:
@namespace url(http://www.w3.org/1999/xhtml);



/* http://userstyles.org/styles/67258 */



@-moz-document domain("imo.im") {



#footer {
    display: none;
}

#body {
    top: 0px !important;
    bottom: 0px !important;
}

#header {
    left: auto !important;
    width: 800px !important;
     background: transparent !important;
    border: none !important;
}

#header > img.sprite_logo_small_2 {
    display: none;
}

#header > ul {
    display: none;
}

#buddypanel {
    margin-top: 40px !important;
}

.buddygroup .bar.white, .buddygroup .bar.white .title  {
   
    font-size: 12px;
    line-height: 20px;
}

.buddygroup .bar.white {
   height: 20px !important;  
}


.button img  {
    margin: 3px !important;
}

#header {
    pointer-events: none;
}

#header .history-search-container .history-search-input {
    margin-top: 25px;
    height: 20px;
    line-height: 20px;
    padding: 0;
    width: inherit;
}

#account-bar, #header .history-search-container .history-search-input {
    pointer-events: auto;
}

#header .history-search-container .history-search-button {
    display: none;
}

#account-gear span {
    color: black;    
}

.buddygrouplist .buddyitem {
    height: auto !important;
    line-height: 20px !important;
}

.buddygrouplist .buddyitem .imgwrap {
    height: auto !important;
    width: 20px;
}

.buddygrouplist .buddyitem .imgwrap img.icon {
    height: 20px !important;
    width: auto !important;
    line-height: 20px !important;
}

}
