css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {


/*sets text color for ALL text*/

/*
* {  color:rgba(51, 51, 51, 1.0) !important;}
*/

/*BACKGROUND begin*/
body {      background: url('http://i1266.photobucket.com/albums/jj532/ins3rt-username/theme/Untitled-1.png') top left #DCDCDC no-repeat fixed !important;}
/*BACKGROUND end*/

/*LOGO begin*/
#logo {
  width: 0px !important;
  height: 0px !important;
  background:url("http://i1266.photobucket.com/albums/jj532/ins3rt-username/theme/q-1.png") !important;
  padding-left: 348px !important;
  height: 53px !important;}
/*LOGO end*/

/*ICON BAR begin - new post icons bar - graphic replacement*/
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1266.photobucket.com/albums/jj532/ins3rt-username/theme/o.png') center center no-repeat !important;
}
.new_post_label{ padding-top:73px !important;}
.new_post_label_icon{background-image:none !important}

/*ICON BAR  end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/
#new_post {  background-color: rgba(255, 255, 255, 0.9) !important;}
/*ICON BAR BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important;}
/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  background-color: rgba(220, 220, 220, 0) !important;}
/*CONTENT*/ 

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(51,51,51,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;}
.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(51,51,51, 1.0)  !important;}
/*SELECTION ARROW end*/

/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.0) !important;
  border-radius: 0px !important;}
.content.is_media, .mask.top, .mask.bottom {  background: none !important;}
/*RADAR end*/

/*NEW CONTENT begin - text post message body background*/
.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(51,51,51, 1.0) !important;}
/*NEW CONTENT end*/

/*DO NOT DELETE THIS BRACKET*/}
