@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alfalfalfa.com") {
    /* ad block */
	div#header_ad,
	ul#hot_tweet,
	div#blog > div.fullbody,
	div#today_list,
	div#thumbnail_list,
	ul#article_footer_amazon,
	div#blog div.fix_microad_b,
	div#blog div.fix_microad_c3,
	div#blog div.fix_microad_d,
	div#blog div.fix_10x10_md1,
	div#blog div.fix_10x10_md2,
	div#blog div.google-user-ad,
	div#blog div.google-user-link,
	div#blog div.tmk,
	div#blog div.keyword,
	div#blog div.hrr,
	div#blog div.menu,
	div#blog div.mv,
	div#blog div.takecommenthead,
	div#blog a[href^='http://www.amazon.co.jp/exec/'],
	div#blog a[href^='http://h.accesstrade.net/sp/'],
	div#blog iframe[width^='728'],
	div#blog iframe[width^='790'],
	div#leftbody > div,
	div#leftbody > dl,
	div#leftbody > a,
	div.Hleft3
	{
		display: none !important;
	}
	
	div#leftbody > div.plugin-recent_articles_image,
	div#leftbody > div.plugin-message,
	div#leftbody > div.plugin-popular_articles,
	div#leftbody > div.plugin-qr_code
	{
		display: block !important;
	}

	/*layout*/
	div#content {
		float: left !important;
	}
	div#left {
		float: right !important;
	}
}
