css:
@-moz-document domain("twitter.com"), domain("www.twitter.com") {
    /* Change hashtag colors */
    a[href][class="hashtag"],
    a[href][class="twitter-hashtag"],
    a[href][class="twitter-hashtag pretty-link js-nav"],
    a[href][class="twitter-hashtag.pretty-link.js-nav"],
    a[href][class=".js-nav.pretty-link.twitter-hashtag"],
    a[href*="hashtag_click"],
    a[href*="src=hash"],
    a[href*="twitter.com/hashtag/"],
    a[rel=hashtag],
    a.hashtag
    {
        color: /*[[YourColour]]*/ !important;
    }
}
