css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("roblox.com") {
    #BCOnlyPlaces {margin-top:0px !important;}
    #ctl00_cphRoblox_GamesPageAd_ReportAdButton {display:none !important;}
    #GamePageAdDiv {height:262px !important;}
    .Column1e .StandardBox {width:714px !important;}
    #GenreDescriptionPanel {display:none;}
    #GamesLeftColumn {border-top-left-radius:5px;border-bottom-left-radius:5px;}
    .Column1e .StandardBoxHeader, .Column1e .StandardBoxHeaderGray span {display:none !important;}
    #GamesLeftColumn {background:none !important;background-color:#eee !important;}
    .StandardBoxGray {background:none !important;background-color:#eee !important;}
    .StandardBoxHeaderGray, .StandardBoxHeaderGray span {line-height:25px;border-top-left-radius:5px;border-top-right-radius:5px;background:none !important;background-color:#4d4d4d !important;}
    #BCOnlyPlaces .StandardBox {background:none !important;background-color:#eee !important;}
    .BCHat {display:none !important;}
    #GamesLeftColumn {margin-top:24px !important;}
    #searchbox {width:300px !important;margin-top:-6px !important;height:22px !important;margin-top:0px;border-top-left-radius:5px !important;border-top-right-radius:5px !important;margin-right:2px !important;}
    .SearchIconButton {top:0px !important;right:7px !important;}
    .StandardTabGray, .StandardTabWhite, .StandardTabGray span, .StandardTabWhite span  {color:#fff !important;background:none !important;background-color:#4d4d4d !important;border-top-left-radius:5px !important;border-top-right-radius:5px !important;margin-left:3px !important;}
    .StandardTabGray:hover, .StandardTabGrayActive, .StandardTabGray:hover span, .StandardTabGrayActive span {margin-left:3px !important;color:#fff !important;border-top-left-radius:5px;border-top-right-radius:5px;background:#6C98CB !important;}
    #PlayTabs {color:#fff !important;margin-bottom:1px;margin-top:-6px;margin-left:-3px;}
    #PlayTabs a {color:#fff !important;}
    #BCOnlyPlaces .StandardBox {height: 188px !important;}
    .AdPanel {display:none;}
    .GameItem .CreatorName {display:block !important;}
    .BlueRightArrow {background:#3C83CB !important;border-top-left-radius:15px;border-bottom-left-radius:15px;border-top-right-radius:120px;border-bottom-right-radius:120px;}
    .BlueLeftArrow {background:#3C83CB !important;border-top-right-radius:15px;border-bottom-right-radius:15px;border-top-left-radius:120px;border-bottom-left-radius:120px;}
    .BlueRightArrow:hover {background:#649dd6 !important;border-top-left-radius:15px;border-bottom-left-radius:15px;border-top-right-radius:120px;border-bottom-right-radius:120px;}
    .BlueLeftArrow:hover {background:#649dd6 !important;border-top-right-radius:15px;border-bottom-right-radius:15px;border-top-left-radius:120px;border-bottom-left-radius:120px;}
}
