css:
@-moz-document domain("tmnforever.tm-exchange.com") {

#MainForm > .Outer {
  width:500px !important;
}

table.NoClass .imgheader {
  max-width:460px !important;
}

table.NoClass .OuterMenu tr:first-child td:nth-child(3),
table.NoClass .OuterMenu tr:first-child td:nth-child(4),
table.NoClass .OuterMenu tr:first-child td:nth-child(9),
table.NoClass .OuterMenu tr:first-child td:nth-child(10),
table.NoClass .OuterMenu tr:last-child td:nth-child(1),
table.NoClass .OuterMenu tr:last-child td:nth-child(5),
table.NoClass .OuterMenu tr:last-child td:nth-child(7)
{
display:none;
}

#ctl03_Windowrow2 > table > tbody > tr > td:nth-child(3),
#ctl03_Windowrow2 > table > tbody > tr > td:nth-child(5),
#ctl03_Windowrow2 > table > tbody > tr > td:nth-child(7) {
display:none;
}

#ctl03_Windowrow3 .WindowContainer > table > tbody > tr:last-child > td:last-child > table {
width:330% !important;
}
 
.WindowForumInfo {
display:none;
}

#ctl03_Windowrow3 td.WindowContainer > table > tbody > tr:first-child {
position:relative;
}

#ctl03_Windowrow3 td.WindowForumTitle:first-child {
position:absolute;
height:18px;
}

#ctl03_Windowrow3 td.WindowForumTitle:first-child a {
line-height:18px;
vertical-align:middle;
}

#ctl03_Windowrow3 td.WindowForumTitle:last-child {
position:relative;
width:288px;
height:20px;

}

#ctl03_Windowrow3 td.WindowForumTitle:last-child td:first-child {
display:none;
}
#ctl03_Windowrow3 td.WindowForumTitle:last-child td:last-child {
float:right;
padding:0 0 3px 0;
}

#PanelBookmark {display:none;}



/* End */
}
