css:
@-moz-document domain("kirik.tea-nifty.com") {

* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}

pre {
white-space : pre !important;
overflow : auto;
}

html {
font-size : 100% !important;
background-color : #e9e9e4 !important;
}

body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e9e9e4 !important;
color : #666 !important;
/*width : 40em !important;*/
max-width : 100% !important;
border : 0 !important;
}


table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }

font[size] { font-size : 100% !important; }

/*table[width] { max-width : 40em !important; }*/



#container
, #container div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}

#container * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #666 !important;
}

#container a:link { color : #7777ee !important; }
#container a:visited { color : #551a8b !important; }
#container a:hover { color : #9955ee !important; }
#container a:hover { background-color : transparent !important; }

.layout-two-column-right #alpha-inner {
    background-color : transparent !important; 
}

#banner h2
, .sidebar > div
, .sidebar > div.cocolog
, div[style="clear: both;"]
{ display : none !important; }

h2.module-header
{ padding : 0 !important; }

h2.module-header
{ border : 0 !important; }

.sidebar li
{ margin : 0 !important; }

#container h2.module-header
{ margin-top : 2em !important; }

#banner
, #recent-entries
{ display : block !important; }


.layout-two-column-right #alpha-inner *{
background-color : #e9e9e4 !important;
}


#container-inner *{
background-color : #e9e9e4 !important;

}

#container{
margin:auto !important;
}


#container-inner {
width: 90% !important;
align:center !important;
}

#banner-header {
margin-top: 20px !important;
}




}
