	margin-bottom: 10px;

	}
form b, .ventre-meny, .hogre-meny, .Tittel{
	text-shadow:#000 1px 1px 1px;
}
	
option	{
	background: #252525;
	}
	
select	{
	border:2px solid #505050;
	border-radius:4px;
	background: #252525;
	}
	
input, textarea, select	{
	box-shadow:#000 1px 1px 1px;
	text-shadow:#000 1px 1px 1px;
	outline:none;
	}

input[type=text] {
	padding: 6px;
	width: 225px;
	background: #252525;
	border: 2px solid #505050;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	color:#CCCCCC;
	margin-bottom:5px;
}
textarea{
	padding: 6px;
	background: #252525;
	border: 2px solid #505050;
	color:#CCCCCC;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
}
input[type=password] {
	padding: 6px;
	width: 225px;
	background: #252525;
	border: 2px solid #707070;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	margin-bottom:5px;
	color:#CCCCCC;
}

a, a:link, a:visited {
	color: #D3D1CE;
	text-decoration: none;
	font-family: Arial, Helvetica, sans-serif;
	border: 0px;
}
	a:hover {
	color: #707070;
	font-family: Arial, Helvetica, sans-serif;
	border: 0px;
	}
input[type=button], input[type=submit], input[type=reset] {
	height: 30px;
	background: #505050;
	color: #CCCCCC;
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border: 0;
	font-family: Arial, Helvetica, sans-serif;
	font-size: 12px;
	align: right;
	padding-left: 15px;
	padding-right: 15px;
	margin:5px;
}
	input[type=button]:hover, input[type=submit]:hover {
	background: #707070;
	}
input[type=reset] {
	background: #37302b;
	border: 1px solid #1e1a18;
}
	input[type=reset]:hover {
		background: #1e1a18;
	}

.bords{
	border: 1px solid #282828;
}

td.Tittel {
