css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("u-cursos.cl") {
/******************** PANTALLA DE BIENVENIDA ***************************/
div#inicio #visitantes {background: url('https://dl.dropboxusercontent.com/s/9291zmetr98e389/instituciones.png') no-repeat 25px 30px  transparent !important;float: left;}
div#inicio #visitantes a img{background-color: transparent !important;visibility: hidden;}
/******************** FIN DE PANTALLA DE BIENVENIDA ************************/
/******************* ICONOS ****************************/
div.cargos_ayudante {background-image: url('https://dl.dropboxusercontent.com/s/veoyaz5pavqlrh3/icono-ayudante.png') !important;width: 19px;height: 19px;}
div.cargos_profesor_de_catedra {background-image: url('https://dl.dropboxusercontent.com/s/ygc87tgiuur2xyk/icono-docente.png') !important;width: 19px;height: 19px;}
div.cargos_alumno {background-image: url("https://dl.dropboxusercontent.com/s/zobekhl7gyf562j/Icono-usuario.png") !important;width: 19px;height: 19px;}
div.cargos_miembro_de_comunidad {background-image: url('https://dl.dropboxusercontent.com/s/63akaczr6s2gwr2/icono-miembro-comunidad.png') !important;width: 19px;height: 19px;}
div.cargos_administrador_de_comunidad {background-image: url("https://dl.dropboxusercontent.com/s/z2gvnu5knrku5r2/icono-administrador-comunidad.png") !important;width: 19px;height: 19px;}
ul.modulos li img {opacity: 0.2 !important;}
div#izquierda div ul li a img {opacity: 0.2 !important;}
ul.modulos li.sel img {border: 2px solid rgba(225, 12, 18, 1.0) !important;}
ul.modulos li.sel span {border: 2px solid rgba(225, 12, 18, .5) !important;}
/******************** FIN ICONOS ******************************/
/******************** CUADRO MENSAJE DE AVISO **********************************/
.maviso {background: url("https://dl.dropboxusercontent.com/s/dpu7hgrg3qvmqo7/aviso.png") no-repeat scroll left center transparent !important;border: 1px solid #3c3c3c;color: #DF8200 !important;}
.mexito {background: url("https://dl.dropboxusercontent.com/s/xz82nn358wcsvu7/exito.png") no-repeat scroll left center transparent !important;border: 1px solid #74CD1B;color: transparent;}
.merror {background: url("https://dl.dropboxusercontent.com/s/ug8ypndd7q119pn/error.png") no-repeat scroll left center transparent !important;border: 1px solid rgb(255, 0, 0);color: rgb(255, 0, 0);}
/******************** FIN CUADRO MENSAJE DE AVISO **********************************/
/******************** BARRA DE MENU ***********************************/
div#izquierda div h1 {font-size: 11px;color: #01A497 !important;background: url("https://dl.dropboxusercontent.com/s/k1zlp7mn74w3nx5/barrita-menu2.png") no-repeat scroll right bottom transparent !important;width: 200px;height: 27px;padding: 0px 0px 0px 13px;line-height: 11px;text-align: left;text-transform: uppercase;}
div#izquierda div h1 a {text-decoration: none;color: #A6CF00 !important;padding: 7px 0px 4px 30px;display: block;white-space: nowrap;overflow: hidden;}
div#izquierda div.off h1 a {color: #BC0000 !important;}
/******************** FIN DE BARRA DE MENU ***********************************/
/************************* SECCION BLOG **************************/
div.blog h1 {font-size: 18px;color: #00B500 !important;padding: 0px;}
/*********************** FIN SECCION BLOG *********************/
/******************** PRINCIPAL ***********************/
body{background: #3C3C3C url('https://dl-web.dropbox.com/get/ucursos/datos_institucion-dark.png?w=AAAtDAgtxFj9f5Pr1kT8JyWQ5Hs2XK5HuPF4ENLUaXQokg') fixed no-repeat 95% 90% !important;margin:1px;}
* {background-color: transparent !important;margin: 0;padding: 0;color:#ABC8F3 !important;}
h1.modulo{color:#00B500 !important;} /*deja el titulo "Foro" en color verde*/
h2.ucursos {background-color:#3C3C3C !important;background-image: url("https://dl.dropboxusercontent.com/s/78awbxhef226nvd/uchile.png") !important;color:#00B500 !important;}
a {color: #BA9603 !important;}/* Modifica el color de los enlaces */
#ucursos {height: 85px !important;width: 190px !important;padding-top: 0px !important;padding-bottom: 0px !important;margin-top: 0px !important;margin-bottom: 0px !important;background: url('https://dl.dropboxusercontent.com/s/uk6e51o1p9flykr/ucursos3.png?token_hash=AAFvrvIVb-rryHMzneXkvBahEr_1uV0mYV8cc_LoK_7C6A&dl=1') no-repeat !important;}/*este bloque reemplaza el logo de ucursos normal, por el que tiene el fondo negro*/
ul#navbar li {display: inline;padding: 0px 3px 0px 9px;background: url("https://dl.dropboxusercontent.com/s/4tgahu8y5s8cj4g/raquo.png") no-repeat scroll left center transparent !important;}
div.hijo div.container, div.responder div.container {background: url("https://dl.dropboxusercontent.com/s/t1okfvks4mgebxq/flecha.png") no-repeat scroll left top transparent !important;}
.boton {background: url("https://dl.dropboxusercontent.com/s/xojbzuzeum07woe/buscar_2.png") no-repeat !important;color: #ABC8F3 !important;text-align: center !important;}/*este bloque reemplaza el boton de agregar*/

/******************** FIN DE PRINCIPAL ***********************/
/************************ CONTROL DE TABLAS ***********************************/
td.opciones {background-color: #3C3C3C !important;}
td.gris {background: none repeat scroll 0% 0% #3C3C3C !important;}
td.gris span{color:#FF8809 !important;}
img.histograma {position: absolute;margin: -75px 0px 0px -125px;background-color: #CCCCCC !important;border: 1px solid #3C3C3C !important;}
#table, th, td {background-color:transparent !important;border: 1px solid #3C3C3C !important;}
td {text-align: left;vertical-align: top;padding: 3px 1px 3px 5px;border-right: 1px solid #3C3C3C !important;border-bottom: 1px solid #3C3C3C !important;background: transparent !important;}
th {background: none repeat scroll 0% 0% #3C3C3C !important;padding: 2px;color: #ffffff !important;}
tbody th {text-align: right;vertical-align: top;border-bottom: 1px solid #3C3C3C !important;padding-right: 10px;}
tr.separador {background-color: rgba(60,60,60,.8) !important;}
td {background-color: rgba(60,60,60,.8) !important;}
div#izquierda div h1 {font-size: 11px;background-color: rgba(0, 0, 0, 1);width: 175px;height: 25px;padding: 0px 0px 0px 13px;line-height: 11px;text-align: left;text-transform: uppercase;border-right: 1px solid rgba(0, 0, 0, 1);border-width: 0px 1px 1px;border-style: none solid solid;border-color: #3C3C3C !important;border-image: none;}
table {border-color: #3C3C3C !important;}
/*************************** FIN CONTROL DE TABLAS ************************************/

div#izquierda div ul li {text-align: left;list-style: none outside none;margin: 0px;color: #3C3C3C !important;float: left;width: 165px;border-right: 1px solid rgb(0, 0, 0) !important;border-width: 0px 1px 1px;border-style: none solid solid;border-color: -moz-use-text-color #3C3C3C !important;background-color: transparent !important;}
div.revista h1 {color: #00B500 !important;font-size: 18px;margin: 25px 0px 5px;padding: 0px;}


/******************** ZONA DE PRUEBAS ***************************/

div#izquierda div ul li.sel a {color: rgb(102, 102, 102);}
div#izquierda div ul li:hover {background-color: #000000 !important;text-decoration: none !important;}

a.audio, pre.source, pre.latex, pre.graphviz {
    background-color: #545264 !important;
    font-family: Courier New,courier;
}


/******************** FIN ZONA DE PRUEBAS ***************************/
}
