/* AGENT_SHEET */
/*
 * Google Video - dark gray redesign (April update)
 *
 * Updated by  : MutantMonkey
 * Author      : Valacar
 * Updated     : April 30, 2008
 * Website     : http://www.valacar.com/userstyles/
 * Description : Modifies the colors, logos, and a few other images on Google Video (http://video.google.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Thanks to   : storm119 for helping find/fix bugs
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://video.google.com/),
	             url-prefix(http://video.google.co.uk/),
	             url-prefix(http://video.google.com.au/),
	             url-prefix(http://video.google.ca/),
	             url-prefix(http://video.google.de/),
	             url-prefix(http://video.google.es/),
	             url-prefix(http://video.google.fr/),
	             url-prefix(http://video.google.it/),
	             url-prefix(http://video.google.nl/),
	             url-prefix(http://video.google.pl/)
{

body
{
	background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAENCAYAAADOoA9GAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABZSURBVHjaYmBgYPjPACP+MYFIEPEflfUflwQRSojgUiyGX4IkJTTRi5P4R39T/tFByT8qa6OJof8GPJgGPjHQI/FTki+pWFCQXlQhsZiBmBFMwLlgwAgQYAAYPe0bQ81rlgAAAABJRU5ErkJggg==") top left repeat-x !important;
	color: #fff !important;
}

/*******************************
 * Links
 *******************************/

/* defaults for all links */
a:link, a:visited, .link { color: #ccc !important; }
a:active, .link:active { color: #fff !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

font, div, span, td { color: #fff !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete
{
	background-color: -moz-Field !important;
	color: -moz-fieldtext !important;
}

/* can't seem to modify iframes, so we'll keep them white */
iframe { background-color: #fff !important; }


/* google bar (including popup menu)
------------------------------------*/
#gbi { background: #343434 !important; border-color: #575757 !important; }
#gbar .gb2 a:hover { background: #676767 !important; color: #fff !important; }
#gbh, .gbh { border: none !important; }
.gb2 div { border-color: #575757 !important; }


/* bars
-------*/
#searchagain, div + #searchagain { background: #2f2f2f !important; border: none !important; padding-bottom: 6px !important; }

#searchagain form { padding-top: 6px !important; }

/*******************************
 * Text colors
 *******************************/

/* main text color */
.nowplaying-description, .Snippet, #commentlist font, #description font {
	color: #ccc !important;
}

/* dimmed text color */
.list-item-host, .playlist-item-host, .Url, .durationetc {
	color: #888 !important;
}

/* alt text color */
.nowplaying-durationanddate, .playlist-item-rating span, .Details, .Details span, .communityRating span, #durationetc, #commentlist font[color="green"] {
	color: #ffc !important;
}

/*******************************
 * Backgrounds
 *******************************/
 
.lpaf_framefull, table[bgcolor="white"], .nowplaying-info, .nowplaying, .playlist-section,
.playlist-footer, #searchagain, .hotvideos-section, .tr-shaded, #video-data
{
  background: #000 !important;
}


/*******************************
 * Border fixes
 *******************************/
 
.nowplaying, #gbh, .playlist-section, #searchagain, .div-section-header, img[border="1"]
{
	border: none !important;
}

.lpaf_framefull, #upnextmenu, #hotstuff .video, .mod_content, .thumbnail_grid,
.embedded_player_container, .menu-button, .Tab
{
	border-color: #444 !important;
}

/*******************************
 * Rankings tabs
 *******************************/

.Tab,
.Tab a,
.menu-button,
.menu-button a
{
	color: #777 !important;
}

.Tab.Selected
{
	background: #444 !important;
}

.Tab.Selected,
.Tab.Selected a
{
	color: #fff !important;
}

/*******************************
 * Buttons
 *******************************/

.div-hotstuff-tab, .menu-normalsb
{
	background: #656565 !important;
	border: 1px solid !important;
	border-color: #848484 #565656 #565656 #848484 !important;
}

/*******************************
 * "Landing frame"
 *******************************/

.expanded-column,
.collapsed-column
{
	background: #222 !important;
	border-color: #444 !important;
}

/*******************************
 * Section header bars
 *******************************/
 
.section-header,
#resultsheadertable,
.TitleBar,
.table-section-header,
.headline,
.hot_videos_title_bar,
.mod_title,
.related-header
{
	background: #606060 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;
	border: 1px solid #000 !important;
	outline: 1px solid #353535 !important;
}

#resultsheadertable tr > td
{
	vertical-align: middle !important;
}

/*******************************
 * Thumbnail images
 *******************************/
 
.grid-item-thumbnail, .playlist-item-thumbnail, [style*="border: 1px solid blue;"], .detailsimage
{
	border: 1px solid #000 !important;
}


/*******************************
 * Playlist tweaks
 *******************************/
 
/* (note: background-color set in active tab) */
.playlist-item, [class="playlist-item"][style*="background: rgb(238, 237, 241)"]
{
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAwCAYAAADQMxCBAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAAtSURBVHjaYlJXV2dkYmBgYAIRCBYTXjEwwYyLi5OFRuCUABMsJMuyEMEFCDAAz9oBV1rTbjUAAAAASUVORK5CYII=) !important;
	background-position: bottom left !important;
	background-repeat: repeat-x !important;
	border: 1px solid transparent !important;
}

#hotstuff .video
{
	border-color: #000 !important;
}

/*******************************
 * Results page
 *******************************/

.SearchResultsTV .Result
{
	border-color: #000 !important;
}

.SearchResultsTV .result-highlighted,
#hotstuff .video-selected
{
	background: #222 !important;
	border-color: #444 !important;
}

/*******************************
 * Other tweaks
 *******************************/
 
/* Change "Gooooooooooogle" to just text */
.gooooogle font { display: none !important; }
.gooooogle img { visibility: hidden !important; }
.gooooogle div { background: none !important; }
.gooooogle .currentpage { color: #fff !important; font-size: 1.5em !important; }
.gooooogle div { height: auto !important; }
.gooooogle td.b, #navbar td { vertical-align: middle !important; }
.gooooogle td.b:first-child + td { padding-left: 1.5em !important; }

/* expander */
#expander
{
	background: #000 !important;
	border-color: #000 !important;
}

/* share links */
a.google-share-b, a.google-mail-b
{
	padding-left: 14px !important;
	border-color: #222 !important;
	background: #000 !important;
	color: #666 !important;
}

/*******************************
 * Replaced images
 *******************************/

