css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("nairaland.com")  {

body{
background: #A39D9F;
font-family:"Times New Roman", Times, serif
}

td.grad{
border: outset;

border: double;
background-image:url('http://i50.tinypic.com/2gxgpsn.png');
background-repeat:no-repeat;
background-position:center top;
background-color: #665E61;
}

.g{
opacity:0.01;
}

.grad {
color:white;
}
.grad a{
color: #C6D6A9;
}

h2{
color:white;
text-decoration:blink
}

.boards th{
background: grey;
}

.boards td{
background: #ffffffff;
border:inset;
}
.boards a{
color: #4D3B0A;
}
.boards tr{
background-color: #665E61;
}
."l pd w" {
display: none;
}

td.l, .l{
border: outset;
text-decoratioin: overline;
text-align: left;
font-weight: 600;
color: #24300F;
background-color: #grey;
}

.narrow{
padding: 20px;

border: inset;
background-color: #F0F0F0;
}

td.bold a.user{
color:#0A0A0A;
}

td.bold a{
color:#E6F0E6;
}

body p {
font-weight: 800;
font-size:14px;
border: outset;
background: white;
}

#postformtitle{
font-weight: 900;
font-size: 18px;
}

input[type="button"], input[type="submit"] { 
    color:#050; 
    font: bold 150% 'trebuchet ms',helvetica,sans-serif; 
    background-color:#fed; 
    border:1px solid; 
    border-color: #696 #363 #363 #696; 
} 
input[type="text"], input[type="submit"] { 
    color:#050; 
    font: bold 110% 'trebuchet ms',helvetica,sans-serif; 
    background-color:#fed; 
    border:1px solid; 
    border-color: #696 #363 #363 #696; 
} 

.small{
display:none;
}
}
