css:
@-moz-document domain(vkontakte.ru) {

/* v. 6 */

body, td {font: 110% Arial !important; line-height: 1.4 !important}
input[type=button], textarea, button {font-family: "Consolas", "Courier New" !important}
textarea {padding: 1px !important; font-size: 120%}

#pageLayout {padding-top: 28px !important}
#pageHeader {background-image: none !important; width: 100% !important; height: 28px !important; position: fixed !important; top: 0 !important; opacity: .75 !important; z-index: 3 !important}
   #pageHeader #home {display: none}

#header span {display: none}
#quickquery {width: 175px !important; margin-left: 17px; position: relative; top: -1px !important}

.topNav a {text-transform: capitalize; height:22px !important}
.topNav a:hover {background: #3e6b95; text-decoration: none !important}
ul.topNav li {padding-top: 0px !important; background: none !important; width: 10% !important; border-left: 1px solid #3e6b95 !important; height: 25px !important}
#quickSearch {padding-top: 0px !important}

#pageLayout, #pageBody, #rightColumn, .dataWrap {width: auto !important; float: none !important}
#rightColumn {position: relative }

#pageBody {margin-left: 132px !important}
   #pageBody #header {padding: 4px 10px !important}
   .wallpost * {color: #888 !important}
   .wallpost a {color: #999 !important}
   .wallpost .text a {color: #000 !important}
   .wallpost .text {font-size: 100% !important; color: black !important}
   .wallpost .actions {border-bottom: none !important}
   .wallpost .info {border-top: 1px solid #ddd !important}
   .wallpost .info .header {background: none !important; border-bottom: none !important}
   .wallpost .info .header b {display: none}

   .upArrow, .dArrow {position: relative; top: -39px; right: -10px}

#sideBar {position: fixed !important; font-size: 75%; top: 30px}
   #sideBar #nav li {margin-left: -2.7em; white-space: nowrap}
   #sideBar #nav ol li {margin-left: 0em}

#album table {width: 100% !important}
#album table tr {display: block !important; float: left !important}
#album table td {display: block !important; float: left !important; height: 200px !important; width: 125px !important}

.editor {width: 90%}
.editor .labelHigh {width: 150px !important}
.editor .labelField {width: auto !important}
.editor .labelField textarea {width: 100%}

#edu, #career {width: 50%; float: left}
#opinions, #places, #wall, #audios {clear: both}
#groups .flexBox {font-size: 85%; line-height: 1 !important}
#groups .flexBox a {display: block; position: relative; left: .75em; top: 1.25em}
#groups .aPad {max-height: 25em !important; overflow: hidden}
.profileName {width: auto !important}
.profileName h2 {font-size: 18px !important}
.profileName h2:after {color: silver; content: ' Ð² ÐºÐ¾Ð½Ñ‚Ð°ÐºÑ‚Ðµ'}

.pageList {font-size: 135%}
.pageList li {padding: 0 .1em !important}
.pageList a {padding: 0 .35em 0 .3em !important}

.bNav {width: 30em !important; margin: 0 auto !important; position: relative; left: 6.5em}

#woww {display: block !important}

.mailbox table {width: 100%}
.inbox .messageActions {display: none !important}
.mailbox table.inbox .messageSnippet {width: auto !important}

#compose {font-weight: bold}

.settingsPanel {width: auto !important; margin-left: 10px !important; margin-right: 10px !important}
    .settingsPanel .editor {width: 100% !important}
    .settingsPanel .editor td {width: auto !important}
    .settingsPanel .editor td small {font-size: 100%}
    .settingsPanel .editor .label {width: 7em !important}

.infotable .label {display: none}
.infotable .dataWrap {font-size: 130%; font-weight: bold}

.grouprow .infotable .dataWrap {display: block; line-height: 1; overflow: hidden !important; height: 3em !important}
.printcontent {width: 200px !important}
.grouprow .actions {display: none}

.grouprow {width: 300px !important; height: 200px !important; overflow: hidden; float: left}
.grouprow .msg, .grouprow .info div {margin-left: 0 !important}
.groupslist .grouprow {margin: 0 10px 10px 0 !important}
 
#photocaptionleft {width: auto}
#photoLocation {width: 50% !important; text-align: right !important; margin-right: 10px !important; border: 0 !important}

.friendTable td {vertical-align: bottom !important}
#nav li#myprofile a.hasedit {width: auto !important}
.feedDay {font-size: 110% !important; font-weight: normal !important}

#quickReply #r {display: block !important}
#quickReply #r div {padding-left: 7px !important}
#quickReply #r div div {padding: 0!important}
#quickReply #br {display: block !important; text-align: left !important}
#quickReply #br .buttonRow {padding-top: 7px; float: left !important}

#moreWall {display: block !important; margin: -25px 0 0 0 !important; padding-right: 5px !important; text-align: right !important}
a.wallFunc {background: none !important}
#wall .fSub h3 div {display: none !important}
#wall .fSub h3 div:first-child {display: block !important}

textarea {width: 100% !important}

#photoinfo {width: auto !important}
#photoaddcomment {width: auto !important}
#photocomment {width: auto !important;margin-right: 200px; float: none !important}

.audioRowWall {width: auto !important}

.ad_box,.ad_title,.ad_desc{display: none !important;}
#banner2, #banner1 {display: none !important;}

}
