css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sweclockers.com") {

/* KNAPPAR */
/* Bas för alla knappar */
.button {
	background: #CCC url(http://i.imgur.com/tdve1.png) !important;
	color: #666 !important;
	border: none !important;
	border-bottom: 1px solid #CCC !important;	
}
/* Rättar till textposition på standardknappar, typ input och submit */
input.button, button.button {
    padding: 0 4pt !important;
}
/* Citera-knappar */
.postbitctrl .buttonQuote {
	background: #CCC url(http://i.imgur.com/U7NU8.png) !important;
	color: #666 !important;
	border: none !important;
	border-bottom: 1px solid #CCC !important;	
}
/* Mouseover på knappar */
.postbitctrl .buttonQuoteSelected, .postbitctrl .buttonQuote:hover, .postbitctrl .button:hover, .postbitctrl .buttonReport:hover {
	opacity: 0.5;
}
/* Anmäl-knappen */
.buttonReport {
	background: #CCC url(http://i.imgur.com/AYkk9.png) !important;
	color: #666 !important;
	border: none !important;
	border-bottom: 1px solid #CCC !important;	
}

/* Toolbar över första-post i trådar */
table.forum_toolbar td {
    background: url(http://i.imgur.com/JjHUG.png) !important;
    background-color: transparent !important;
    padding: 4px !important;
}
table.forum_toolbar, .vbmenu_control a {
    border: none !important;
}

/* INLÄGG */
/* Toppranden på inlägg */
td.thead {
    background: #CCC url(http://i.imgur.com/tdve1.png) !important;
}
td.thead, td.forum_post_head_right.thead a {
    /*color: rgb(53, 53, 53) !important;*/
    color: rgb(70, 70, 70) !important;
}

/* Bra (gillade) inlägg */
.forum_post.isLiked .thead{
    background: #CCC url(http://i.imgur.com/lRRsR.png) !important;
    border-bottom: 1px solid rgb(224, 224, 224) !important;
}
.forum_post.isLiked .thead .statusicon {
    background: url(http://i.imgur.com/R6SM1.png) -2px -3px no-repeat !important;
    height: 13px !important;
    overflow: visible;
}
/* Inlägg av TS */
.forum_post.isTS .thead{
    background: #CCC url(http://i.imgur.com/U9WIw.png) !important;
    border-top: none !important;
    border-bottom: 1px solid rgb(224, 224, 224) !important;
}
/* Färger postbit */
td.forum_post_main.alt1, td.forum_post_control.alt1 {
    background: none repeat scroll 0% 0% rgb(244, 244, 244) !important;
    color: rgb(45, 45, 45) !important;
    border-top: 1px solid rgb(224, 224, 224) !important;
}
table.forum_post tbody tr td.forum_post_control.alt1 {
    border: none !important;
}
div.forum_post_body.text div.bbquote {
    background: rgb(224, 224, 224) !important;
}
/* Färger userbit */
td.forum_post_profile.alt2, td.forum_post_status.alt2 {
    background: none repeat scroll 0% 0% rgb(235, 235, 235) !important;
    /*border-right: 1px solid rgba(255, 255, 255, 0.9) !important;*/
    border-right: 1px solid rgb(224, 224, 224) !important;
}
td.forum_post_profile.alt2 p.title {
    color: rgb(70, 70, 70) !important;
}
td.forum_post_profile.alt2 h4 a {
    color: rgb(70, 70, 70) !important;
    text-decoration: none !important;
}

/* Border runt inlägg */
div#posts div.center div.page div div table.forum_post {
    border: 1px solid rgb(224, 224, 224) !important;
    border-top: none !important;
}

/* Polls */
td.panelsurround {
    background: none repeat scroll 0% 0% rgb(244, 244, 244) !important;
    color: rgb(45, 45, 45) !important;
    border: none !important;
    border-bottom: 1px solid rgb(224, 224, 224) !important;
}
tbody#collapseobj_newpost_options.tborder {
    border: none !important;
}
div#collapseobj_threadreview table.forum_post {
    border: 1px solid rgb(224, 224, 224) !important;
    border-top: none !important;
}
div.columns.forumWrapper.fixedForum div.column.fluid div.inner div.gutter div.center div.page div table.tborder {
    border-bottom: none !important;
}

table.tborder tbody tr td.tcat {
    background: url(http://i.imgur.com/JjHUG.png) !important;
    background-color: transparent !important;
    padding: 6px !important;
    font-size: 13px !important;
}
table.tborder thead tr td.tcat {
    background: url(http://i.imgur.com/JjHUG.png) !important;
    background-color: transparent !important;
    padding: 6px !important;
    font-size: 13px !important;
}
table.tborder tbody tr td.alt1 {
    background: none repeat scroll 0% 0% rgb(244, 244, 244) !important;
    border-bottom: 2px solid rgb(235, 235, 235) !important;
}
table.tborder tbody tr td.alt2 {
    background: none repeat scroll 0% 0% rgb(235, 235, 235) !important;
    border-bottom: 2px solid rgb(244, 244, 244) !important;
}
div.gutter div.center div.page div table.tborder {
    border: none !important;
    border-bottom: 1px solid rgb(179, 175, 166) !important;
}
div.subforums table tbody tr td.tfoot, div.subforums table tbody tr td.tcat {
    background: url(http://i.imgur.com/JjHUG.png) !important;
    background-color: transparent !important;
    padding: 6px !important;
    font-size: 13px !important;
}

/* Biberu (utvecklare) */
td.forum_post_profile.alt2 h4 a[href="http://www.sweclockers.com/forum/member.php?u=16329"] {
    color: #f66 !important;
    text-decoration: none !important;
}
/* Nyhets-bot */
td.forum_post_profile.alt2 h4 a[href="http://www.sweclockers.com/forum/member.php?u=13448"] {
    color: #5f5f5f !important;
    text-decoration: none !important;
}
/* De tre musketörerna */
td.forum_post_profile.alt2 h4 a[href="http://www.sweclockers.com/forum/member.php?u=16766"],
td.forum_post_profile.alt2 h4 a[href="http://www.sweclockers.com/forum/member.php?u=8925"],
td.forum_post_profile.alt2 h4 a[href="http://www.sweclockers.com/forum/member.php?u=8184"]
{
    color: #0074d6 !important;
    text-decoration: none !important;
}

/* PROFIL */
/* Main content */
div .inner div.gutter div.maincontent {
    background: none repeat scroll 0% 0% rgb(244, 244, 244) !important;
}
/* Profilmeddelanden */
#feed .wallTopic .body {
    background: none repeat scroll 0% 0% rgb(235, 235, 235) !important; 
}
/* Textfärg i loggen */
li.feedItem.forumPost {
    color: rgb(70, 70, 70) !important;
}
li.feedItem.forumPost span.profile, li.feedItem.forumPost a.profile, li.feedItem.forumThread a.profile, li.feedItem.pm a.profile {
    color: rgb(60, 60, 60) !important;
}

/* GLOBALT */
/* Menyflikar */
div.menuShadow div.menu ul.menu.clear li.menuItem {
    background: rgb(244, 244, 244) !important;
}
div.menuShadow div.menu ul.menu.clear li.menuItem:hover {
    background: rgb(235, 235, 235) !important;
}
div.menuShadow div.menu ul.menu.clear li.menuItem.current, div.menuShadow div.menu ul.menu.clear li.menuItem.current:hover {
    background: rgb(247, 246, 245) !important;
}
/* Menyflikar submenu */
div.head_main.clear div.content.clear div#submenu li {
    background: rgb(244, 244, 244) !important;
}
div.head_main.clear div.content.clear div#submenu li:hover {
    background: rgb(235, 235, 235) !important;
}
div.head_main.clear div.content.clear div#submenu li.current, div.head_main.clear div.content.clear div#submenu li.current:hover {
    background: rgb(255, 255, 255) !important;
}
/* Meny popups */
div.vbmenu_popup table tbody tr td.vbmenu_option {
    background: none repeat scroll 0% 0% rgb(235, 235, 235) !important;
    color: rgb(70, 70, 70) !important;
}

/* FORUMINDEX */
tr[align="center"] td.forumstatus.alt2,
tr[align="center"] td.lastpostby.alt2,
tr[align="center"] td.count.alt2 {
    background: none repeat scroll 0% 0% rgb(244, 244, 244) !important;
    border-bottom: 2px solid rgb(235, 235, 235) !important;
}
tr[align="center"] td.forumtitle.alt1Active,
tr[align="center"] td.count.alt1 {
    background: none repeat scroll 0% 0% rgb(235, 235, 235) !important;
    border-bottom: 2px solid rgb(244, 244, 244) !important;
}
img[src="/forum/images/statusicon/forum_new.gif"] {
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 45px !important;
	padding-top: 45px !important;
	background: url(http://i.imgur.com/QrdMh.png) no-repeat !important;
}
img[src="/forum/images/statusicon/forum_old.gif"] {
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 45px !important;
	padding-top: 45px !important;
	background: url(http://i.imgur.com/4vDgA.png) no-repeat !important;
}

/* FRAMSIDA */
div.gutter div.maincontent div.maincontentinner div#news div.article div.body h2 a,
div.gutter div.maincontent div.maincontentinner div#news div.article div.body h1 a,
div.gutter div.maincontent div.maincontentinner div#news div.article div.body ul.detail li.cat a,
div.gutter div.maincontent div.maincontentinner div#news div.article div.body ul.detail li.date,
div.gutter div.maincontent div.maincontentinner div#news div.article div.body div.preamble.bbcode p.bbparagraph,
div.gutter div.maincontent div.maincontentinner div#news div.article div.body div.preamble.bbcode p.bbparagraph a.bblink {
    color: rgb(70, 70, 70) !important;
}
div.gutter div.maincontent div.maincontentinner div#news div.article div.body div.preamble.bbcode p.bbparagraph a.bblink.readmore {
    color: rgb(31, 89, 148) !important;
}

/* NYHETER */
div#articleBody div.bbcode table.bbtable thead tr th,
div#articleBody div.bbcode table.bbtable thead tr th.first,
div#articleBody div.bbcode table.bbtable thead tr th.last {
    background: url(http://i.imgur.com/JjHUG.png) !important;
    background-color: transparent !important;
    padding: 3px !important;
    font-size: 12px !important;
    border-top: none !important;
}

}
