css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("facebook.com") {

.highlightIndicator, 
.tinyman:after, 
.fbTimelineSideAds,
.ego_column,
.buttonWrap, 
.fbTimelineSpine, 
.spinePointer, 
.topBorder, 
.bottomBorder, 
#footerContainer, 
.middleLink, 
.slimHeader #pageNav li.tinyman::after, .slimHeader #pageNav li.middleLink::after, 
.slimHeader #pageNav .middleLink a, 
.moreSectionsLink,
#pageLogo,
#pagelet_rhc_footer,
#pagelet_reminders,
#navSearch button,
#headNav .uiProfilePhoto,
#rightCol
{
display:none !important;
}

#navSearch
{
position:absolute !important;
top:10px !important;
left:10px !important;
}
#navSearch input, #navSearch .uiTypeahead
{
   width:120px !important;
}
#navSearch input, #navSearch .innerWrap
{
   background-color:#ccc !important;
}
#navSearch{border:0 !important;}
.uiSearchInput
{
   border:0px !important;
   border-top:0px !important;
}
#navSearch .innerWrap
{
   border:0px !important;
}


.uiUfi .ufiItem {
background-color:#fafafa;
}

a {color:#bbb;}

a.jewelButton{opacity:0.1;}

.nsh #pageNav .navItem a,
.nsh #pageNav #navAccountLink {
color:#fefefe;
}

#blueBar
{
background-color:#ccc !important;
border-bottom:#bbb !important;
left:auto !important;
top:auto !important;
position: relative !important;
}

#contentArea
{
   width:700px !important;
}


.nsh #pageNav .navItem a:hover, .nsh #pageNav .navItem a:focus, .nsh #pageNav .navItem a:active, .nsh #pageNav #navAccountLink:hover, .nsh #pageNav #navAccountLink:focus, .nsh #pageNav #navAccountLink:active
{
background-color: #ccc;
}

#leftCol
{
opacity:0.1;
}
#leftCol:hover
{
opacity:1;
}
img, i, .uiUfiLikeIcon
{
	opacity:0.1;
}
img:hover,
i:hover,
.uiUfiLikeIcon:hover,
.photoWrap:hover img,
.uiPhotoThumb:hover img,
.coverBorder:hover img
{
	opacity:1;
}

}
