css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(www.kafic.net), domain(www.kafic.net)
{
{
	font-family: Comic Sans, Comic Sans MS, cursive !important;
	background:#000000 !important;
}
#main {
background: #FFFFFF url(http://img257.imageshack.us/img257/308/41226331ot7.png) repeat-x !important;
}
.table_info .odd, .odd{
	background:#000000 !important;
}

.table_info a:link, .table_info a:visited
{
	color:#000000 !important;
}
#liveboard .panel-basic, .panel-basic, .selector
{
	background:none !important;
}
a:link.user_male, a:visited.user_male
{
	color:#3366FF !important;
}
a:link.user_female, a:visited.user_female
{
	color:#FF0000 !important;
}
.ul_list a:hover, .ul_list .current_link a
{
	color:#999999 !important;
	background-color:#FFFFFF !important;
}

.table_info .odd, .odd{
	background:#CCFFFF !important;
}
.table_info a:link, .table_info a:visited
{
	color:#999999 !important;
}
.border_menu .icon a:hover, .border_menu .icon.current_link a, .border_menu .icon.current_link a:link, .border_menu .icon.current_link a:visited
{
		background-color:#CCFFFF !important;
}
.ul_list a
{
	color:#00ff00 !important;
	background-color:#000000 !important;
	border: 1px solid #1c1c1c !important;
}
.ul_list a:hover, .ul_list .current_link a
{
	color:#000000 !important;
	background-color:#CCFFFF !important;
}
.table_info a:link, .table_info a:visited
{
	color:#ffffff !important;
}

#video_panel img
{
	border: #CCFFFF !important;
}
.bordit
{
	border:1px solid #CCFFFF !important;
	padding:1px !important;
}
.border_menu div, .border_div, tr.border_div td
{
	border-bottom: 1px solid #000000 !important; 
}
#broadcast_container
{
	font-size: 12px !important;
	font-weight:600 !important;
	background-color: !important;
	color: ##FFFFFF!important;
}
}
