css:
@-moz-document domain("facebook.com") {



#facebook {background-image: url(http://www.luisposter.de/images/product_images/popup_images/DP011.jpg) !important; background-attachment: fixed !important;}

#content {background-color: #FFFFFF !important;}

#home_main .clearfix a {color: #009900!important}



.photo_widget img {

border: 5px solid #FFFFFF;

}

.photo_widget img:hover {

border: 5px solid #99cc99;

}



#ad_0, #ad_1, #ad_2, #ad_3, #ad_4, 

#ad_5, #ad_6, #ad_7, #ad_8, #ad_9,

#ssponsor,

.advert,

.ad_body,

.ad_capsule,

.ad_media,

.comments_ad_image,

.sponsors,

.sponsor_absolute { display:none !important; }



/* Remove the unnecessary New Stuff and Next Step

   sidebar items from the Facebook Home page. */

#new_stuff_content,

.nextstep { display:none !important; }

#more_section { display:none !important; }

#expandable_more { display:block !important; }

#more_link { display:none !important; }

div#ssponsor { display:none !important; }



/* ####################################################### */ 





#sidebar a.go_home {



background:#3B5998 url(http://i7.photobucket.com/albums/y277/devil_dreg/facebooklogo.gif) no-repeat scroll left top !important;

}



#navigator {

background:#3B5998  url(http://i7.photobucket.com/albums/y277/devil_dreg/facebooknav.gif) repeat scroll !important;

}



#navigator .main_set li a.edit_link {

color:#BDBDBD !important;

}



#navigator .secondary_set li a {

color:#c7c7c7!important;

}



#navigator .main_set li a.active, #navigator .main_set li a:hover {

background:#00CC00 none repeat scroll 0% !important;

}



h2 a {

color: #4c618d !important;

}



#sidebar .divider_bar {

display: none;

}



#app_non_nav_list { margin-top: -10px !important; }



#userprofile {

background-color: #FFFFFF !important;

}



.holder:hover {

background-color: #00CC00 !important;

}



#userprofile a {

color: #009900 !important;

}



.link_title {

color: #00cc00 !important;

}

.link_title:hover {

color: #FFFFFF !important;

background-color: #00cc00 !important;

}



*[class*="flex_shut"] > *[class*="box_head clearfix"] {

background-color: #DDFFDD !important;

}



*[class*="flex_open"] > *[class*="box_head clearfix"] {

background-color: #AAFFAA !important;

}



*[id*="title_app"] {

color: #009900 !important;

}

}
