css:
/*
 * Craigslist - Berrylicious
 *
 * Author      : m.t.freytag
 * Updated     : June 15, 2012
 * Description : Better stlying with a for craigslist (http://www.craigslist.org/)
 * Version     : .55
 */

@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("craigslist.org"), 
domain("craigslist.at"), 
domain("craigslist.ca"), 
domain("craigslist.ch"), 
domain("craigslist.cz"), 
domain("craigslist.es"), 
domain("craigslist.fi"), 
domain("craigslist.gr"), 
domain("craigslist.it"), 
domain("craigslist.jp"), 
domain("craigslist.pl"), 
domain("craigslist.pt"), 
domain("craigslist.se"), 
domain("craigslist.co.in"), 
domain("craigslist.co.kr"), 
domain("craigslist.co.th"), 
domain("craigslist.co.uk"), 
domain("craigslist.co.za"), 
domain("craigslist.com.au"), 
domain("craigslist.com.cn"), 
domain("craigslist.com.mx"), 
domain("craigslist.com.ph"), 
domain("craigslist.com.sg"), 
domain("craigslist.com.tr"), 
domain("craigslist.com.tw")

{
/* Begin Styles */



.hp #search { 
    margin: 20px 0;
}

.hp form#search input#query {
    -webkit-appearance: none;
    border-radius: 5px;
    border: none;
    line-height: 22px;
    margin: 3px 0;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, .6);
    width: 100%;
}

body {
    background: rgba(76, 22, 82, 1);
    font-family: Helvetica;
    color: #fff;
    margin: 0;
}
#header #logo {color:#FFF}

.bchead {
    background: #C60052;
    padding: 10px;
    border-bottom: none;
    margin-bottom: 0;
    box-shadow: 0 2px 5px  rgba(0,0,0,.5);
}

a, a:link {color: rgb(234,255,135);text-decoration:none;}

a:hover, a:visited:hover{text-decoration:underline;}

p.row span{
    vertical-align:center;
}
/*********
 HOMEPAGES
 *********/
.hp .ban a:hover { background: transparent; }

.hp a:hover { color:rgb(234,255,135); }

.hp .col ul li a:hover {
	border-bottom: 1px solid #ccc;
	background: transparent;
}
.hp .ban a {padding:3px 5px;}
.hp #ppp .ban{
    padding: 3px 5px;
    color:rgb(234,255,135);
}
.hp .ban a:hover, .hp #rightbar h5.ban:hover{ 
    text-decoration:none;
    background: rgb(234,255,135);
    color:  rgb(198,0,82);
    border-radius: 5px;
}

.hp #leftbar, .hp #rightbar {
    margin: 0 10px;
    background: transparent;
    border:none;
}

.hp #leftbar font[color=orange], span.p {color: rgb(255,113,75)}

.hp #leftbar li:hover,
.hp #leftbar a:hover,
.hp #rightbar ul.acitem > li > a:hover { background: transparent; }

.hp #rightbar li.s{
    font-size: 12px;
    line-height: 20px;
}
.hp #rightbar li.s h5.ban{ margin: 0 0 10px 0}

.hp div#calttl {
    background: rgb(198,0,82);
    border-radius: 5px 5px 0 0;
    margin:0;
}
.hp div#calttl:hover {
    background:  rgb(234,255,135);
}
.hp table.cal{
    background: rgb(198,0,82);
}
.hp .cal .dys th{
    color:#fff;
}
.hp .cal td {
    background: rgb(76, 22, 82);
}
.hp .cal td.tdy {
    background: rgb(198,0,82);
}

.hp .cal td.tdy a{
color:#fff;
}

.hp .cal td:hover {
	background: rgb(129, 91, 123);
}
.hp .cal td a:hover {
    text-decoration: none;
}
.hp #rightbar h5.ban, .hp #rightbar h5.active {
    padding: 3px 0;
    font-size: 14px;
    background: rgb(198,0,82);
    color: rgb(234,255,135);
}
.hp #leftbar #leftlinks {text-align:Left;}
.hp .col ul li {
    line-height:22px;
    padding-right: 14px;
}
.hp .col ul li a{}
.hp .col ul li a:hover{}

.hp .col ul li a, .hp .col ul li a:hover {
    border:none;
}

.hp #postlks li {background: rgb(198,0,82)!important; border:none !important;}
.hp #postlks li:hover {background: rgb(234,255,135) !important;}
.hp #postlks li a:hover{ text-decoration: none; color: rgb(198,0,82);}

/*********
TABLES OF CONTENT
 *********/
.ban, .toc h4, .toc .sh {
    border:none;
    background: rgb(198,0,82);
    border-radius: 5px;
    box-shadow: 0px 0px 5px 1px rgba(0, 0, 0, .2);
}

.toc h4, .toc .sh {
    padding: 9px 25px;
    font-size: 20px;
    text-transform: uppercase;
}

.toc .gc a, .toc .gc a:hover {
    text-decoration:none;
    background: rgba(255,255,255,.4);
    border-radius:12px; 
    padding: 2px 10px;
}

.toc .gc a:hover {background: rgb(198,0,82);}

.toc #ef {color:transparent;}

.toc #messages .hl { color:transparent; line-height:27px;}

.toc #ef span.hl, .toc #messages span.hl {margin:0;padding:0;}

.toc #ef a, .toc #messages .hl a {
    background: rgba(255,255,255,.4);
    border-radius:12px; 
    padding: 2px 10px;
    font-size: 14px !important;
    color: rgb(234,255,135) !important;
    font-weight: normal !important;
}
.toc #messages .hl a:hover {
    text-decoration:none;
    background: rgb(198,0,82);}

 .toc #messages .hl a font {
    font-size: 14px !important;
    color: rgb(234,255,135) !important;
    font-weight: normal !important; 
}

.toc #searchfieldset {
    background: transparent;
    border:none;
    margin:0;
    padding:0;

}
.toc legend {
    font-weight: bold;
    text-transform: uppercase;
    opacity: .8;
    text-align: center;
    margin: 0;
    padding: 0;
    font-size: 37px;
    line-height: 43px;
}

.toc #searchtable {
border: none;
background: rgba(255,255,255,.2);
white-space: nowrap;
position: relative;
padding: 10px;
border-radius: 5px;}

.toc .row .i img {
    border-radius: 5px;
    box-shadow: 0 0 0 2px rgba(255, 255, 255, .3);
}
.toc #floater {
    border-radius: 10px;
    padding: 5px;
    background: rgb(129, 91, 123);
    box-shadow: 0 0 5px rgba(0,0,0,.5);
}
.toc #floater img {
    border-radius: 8px;
    border: none;
}

.toc .row .i {
    width: 50px;
    height: 50px;
    margin: 0 10px 0 0;
    border-radius: 5px;
    box-shadow: inset 0px 0px 25px 3px rgba(0, 0, 0, .3);
    background-repeat: no-repeat;
    background-size: 40px;
    background-image: url(data:image/png;base64,);
}

.posting{ padding: 0 10px}
.posting .bchead {margin: 0 -10px;}
.posting a#ef {
    background: rgba(255,255,255,.4);
    border-radius:12px; 
    padding: 2px 10px;
    font-size: 14px !important;
    color: rgb(234,255,135) !important;
    font-weight: normal !important;
}
.posting a#ef:hover {
    text-decoration: none;
    background: rgb(234,255,135);
    color: rgb(198,0,82)!important;
}
.posting #flags {
    margin: 17px 7px 17px 17px;
    padding: 7px;
    border: none;
    font-size: small;
    background: rgb(198,0,82);
}

.posting #tsb {
    background: transparent;
    font-size: small;
}

.posting fieldset {
   background: transparent;
}
.posting legend {
    font-size:16px;
}
.posting #userbody {
    background: #fff;
    color: #222;
    padding: 10px; 
    margin: -10px -10px 10px -10px;
    box-shadow: inset 0 2px 10px rgba(0, 0, 0, .75);
}
}
