css:
@-moz-document domain("news.gogo.mn"), domain("economics.gogo.mn"), domain("survey.gogo.mn") {

.gn_news_brief, .gn_most_news { background: black !important; color: #606060 !important; }
.gn_news_brief h3 a { color: #606060 !important; }
.gn_box div:nth-child(2), .gn_box div:nth-child(1) { background: black !important; }
.gn_box_head_title { border-bottom: 1px solid /*[[accentColor]]*/; }
.gn_box_head_title a { color: /*[[accentColor]]*/ !important; }
.most_wrapper, .gn_box { border: 1px solid #404040 !important; }
.gncont { background: #101010 !important; }
.gn_news_brief { border-bottom: 1px dashed #404040 !important; }
.gntiles { background: black !important; }
.gn_nav { background: gray !important; color: silver !important; }
.gn_nav a { color: white !important; }
body { background: #050505 url(http://i662.photobucket.com/albums/uu346/ebattulga/dark_wool-1.jpg) !important; }
embed { display: none; }
.content > div { border-bottom: 1px dashed #404040 !important; }
.gn_most_news li { border-bottom: 1px solid #404040 !important; }
.gn_news_brief .extras a, .gn_comment_area a, .foot_txt, .go_footer_table, .go_footer_table a, .gn_box_head_title, .d-date, .gn_box_head_title a, .gn_more, .timestamp span { color: /*[[accentColor2]]*/ !important; }
.gn_box_head_title { border-bottom: 1px solid /*[[accentColor]]*/ !important; }
.side_bar_div { display: none !important; }
.west_left_menu { background: #101010 !important; }
.left_menu_title { border-bottom: 1px solid #202020 !important; }
ul.gn_most_news li a, .poll_title, .gom_search_list_table0, .gom_search_list_table1, .left_menu_title .unselect_a { color: #606060 !important; }
ul.gn_tab li a { background: black !important; border: none !important; color: #404040 !important; }
.gn_comment_area, .gn_read_news { background: black !important; color: #606060 !important; }
.gogo_header_mid img[alt="GoGo"] { background: url(http://i662.photobucket.com/albums/uu346/ebattulga/darklogo.png) !important; width: 0px !important; height: 95px !important; padding-left: 219px !important; }
.searchnew_pad { display: none !important; }
.gn_menu { background: gray !important; }
.gn_menu a { color: white !important; }
.widget_cat_title, .gn_category { background: /*[[accentColor]]*/ !important; }
.gn_news_brief h3 a, .gn_box .content h3 a { color: /*[[accentColor]]*/ !important; margin-bottom: 4px !important; }
font[color="#006699"] { color: white !important; }
.gncont > div:nth-child(3) { border: 1px solid #202020 !important; }
div[style="width: 632px; *width: 664px; background-color: #a2a4a7; margin-top: 5px; padding: 16px;"] { background: black !important; }
td[style="border-top: 3px solid #00AEEF; padding: 10px 0px; line-height: 17px;"]{border-top:3px solid /*[[accentColor]]*/ !important;}
}
