css:
/**
 * WordReference.com: hide annoyances and native look
 * v. 0.1pre0a
 * 2008-04-28 04:02 -0400
**/
@-moz-document domain("wordreference.com") {
  .bannertop, .ffoxS, #Otbl+p+table, #Otbl+p {
    display: none !important;
  }
  .namecell, .namecell * { 
    background: highlight !important;
    color: highlighttext !important;
    font-size: 12pt !important;
    height: auto !important;
    border: none !important;
  }
  .searchbox {
    -moz-appearance: toolbox !important;
    border: none !important;
    height: auto !important;
    padding: 0 !important;
  }
  .searchbox>* {
    -moz-appearance: toolbar !important;
    margin: 0 !important;
  }
  .searchbox input { 
    padding: 1pt !important;
  }
  .searchbox select {
    position: relative !important;
    top: -1pt !important;
  }
  .layout { 
    color: -moz-fieldtext !important;
    background: -moz-field !important;
  }
  .Rtbl { 
    color: black !important;
  }
  .closeWordsH, .actionsH {
    -moz-appearance: tab !important;
    color: -moz-dialogtext !important;
    z-index: 12345 !important;
    margin-right: 2pt !important;
    padding: 1pt !important;
    padding-left:  4pt !important;
  }
  .closeWords, .actions {
    -moz-appearance: tabpanels !important;
    color: -moz-dialogtext !important;
    margin-top: 0pt !important;
    z-index: -123 !important;
    font-family: sans-serif !important;
    font-size: 8pt !important;
  }
}
