css:
/* 这一部分把番剧的分集列表移动到右上方，弹幕列表在下方 
*/
@-moz-document url-prefix("https://www.bilibili.com/bangumi/") {
#app .plp-r {
    display: -webkit-box;
    display: -webkit-flex;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-flex-direction: column;
    flex-direction: column;
    -webkit-box-align: start;
    -webkit-align-items: flex-start;
    align-items: flex-start;
}

#eplist_module {
    -webkit-box-ordinal-group: 1;
    -moz-box-ordinal-group: 1;
    -ms-flex-order: 1;
    -webkit-order: 1;
    order: 1;
    width: 100%;
}

#danmukuBox {
    -webkit-box-ordinal-group: 2;
    -moz-box-ordinal-group: 2;
    -ms-flex-order: 2;
    -webkit-order: 2;
    order: 2;
    overflow: visible;
    width: 100%;
}

.ep-section-module{
    -webkit-box-ordinal-group: 3;
    -moz-box-ordinal-group: 3;
    -ms-flex-order: 3;
    -webkit-order: 3;
    order: 3;
    overflow: visible;
    width:100% 
}

#recom_module,#paybar_module {
    display: none !important;
}
}


/* 这一部分写的略早，记不得是什么了 */

@-moz-document domain("www.bilibili.com") {
#aside-area-vm,
#aside-area-vm *,
div[id="aside-area-vm"] {

    display: none !important;
}


.anime-bg #preorder {
    width: 100% !important;
}


.live-room-app .app-content .app-body .player-and-aside-area .left-container {
    width: 100% !important;
}


/*这一段把小窗播放的窗口拉大了，但是有点问题，需要手动把视频从右下角拖动一下 */
.v-wrap  #playerWrap >  .mini-player,.v-wrap  #playerWrap >  .mini-player .player{
            width: 700px!important;
    height:auto!important;
    }
}


/*这是脚本最早的部分，记不得细节了 */

@-moz-document domain("bilibili.tv"), domain("bilibili.kankanews.com") {
#indexgg1,#indexgg2,#indexgg3,#indexgg4,#indexgg5,#indexgg6,#indexc2,.c-link,.gg-ads,#topgg,
.r-gg1,#listgg1,#listb2,#listb3,
#alistads,#googlegg,#arcgg3,#contgg1,.ad ,
iframe[src="http://www.bilibili.tv/bdad.html"],
iframe#cproIframe1,
.ad_top,
#bdad,

div[class^="ad-"]


{ display: none !important;}
.v_bgm_l {

position:fixed !important;
width:60px !important;
height:60px !important;
right:120px !important;
bottom:20px !important;
  z-index:99 !important;
}

.v_bgm_l:hover{

width:auto !important;
height:auto !important;
background:#fcfccc !important;

-webkit-transition: width .5s height .1s ;
-moz-transition: width  .5s height .1s ;    
}
.v_bgm_l:hover .v_bgm_zt{display:none !important}
}

@-moz-document domain("acfun.tv") {
iframe[src="http://www.linekong.com/adout/index.shtml"],
#outjswrapid,
.ad,
#ad-top,
#ad-comment
{
    display: none !important;
}
}
