/* Vista Transparency theme
Please post comments, bugs or fixes @  http://userstyles.org/styles/6771

This style makes toolbars, tabbar, findbar, statusbar, menulists and download statusbar transparent
Tested on default theme only and firefox 3.0 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Hide menubar using PersonalMenu anyway */
#toolbar-menubar {
  background: none !important }

/* (context) menu's transparent */
#contentAreaContextMenu,
popup,
menupopup {
  opacity: 0.85 !important }

/* Get rid of fat border */
#navigator-toolbox,
#nav-bar,
#PersonalToolbar {
  margin-bottom: -1px !important }

#browser {
  border: 0 !important }

/* Tabbar and toolbar looks */
.tabbrowser-tabs,
#PersonalToolbar,
#FindToolbar,
sidebarheader,
statusbarpanel,
.tabbrowser-tab[selected="true"],
.tabs-bottom {
  -moz-appearance: none !important;
  background: url("data:image/png;base64,    iVBORw0KGgoAAAANSUhEUgAAAAEAAAH0CAIAAABzX9DAAAAAAXNSR0IArs4c6QAAAARnQU1BAACx    jwv8YQUAAAAgY0hSTQAAeiYAAICEAAD6AAAAgOgAAHUwAADqYAAAOpgAABdwnLpRPAAAABh0RVh0    U29mdHdhcmUAUGFpbnQuTkVUIHYzLjMwQIRHEAAAAHxJREFUSEtj4ArtYxjFo2EwmgZG08BoGhhN    A6NpgPZpgDuwhYHbu5KBxy2Pgdcxg4HfNpGB3yqKgd8slEHQJICB39ibgd/Qi0FAz41BSM+FQVDX    iUFQx5FBUMuOQUjTmkFYw5pBSN2CQUDNhEFI1ZhBSMWIQVjZgEFESRcAX3/GRaJ4VNkAAAAASUVO    RK5CYII=") bottom !important }

.tabbrowser-tab:not([selected="true"]):hover {
  background: url("data:image/png;base64,    iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAIAAAC0rgCNAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAB3R    JTUUH2AIDBAEFNLSlfgAAAAd0RVh0QXV0aG9yAKmuzEgAAAAMdEVYdERlc2NyaXB0aW9uABMJISMAAA    AKdEVYdENvcHlyaWdodACsD8w6AAAADnRFWHRDcmVhdGlvbiB0aW1lADX3DwkAAAAJdEVYdFNvZnR3Y    XJlAF1w/zoAAAALdEVYdERpc2NsYWltZXIAt8C0jwAAAAh0RVh0V2FybmluZwDAG+aHAAAAB3RFWHRT    b3VyY2UA9f+D6wAAAAh0RVh0Q29tbWVudAD2zJa/AAAABnRFWHRUaXRsZQCo7tInAAAAW0lEQVR4nCX    DPQqDQBCA0fHT9Ydd8Qg2KXN/RK9jI9qFODOuhBQ+eDLNC9t+cOUbyxk15zTna85HHfWL056af+WqJS    KCFBVCQAhUoaWuO5o2EmNPSgMpDYzv1x/uYCVPmghWvQAAAABJRU5ErkJggg==") center !important }

#FindToolbar > *,
.findbar-container > *,
statusbarpanel,
sidebarheader,
.tabbrowser-tab .tab-text,
#PersonalToolbar toolbarbutton > label {
  color: -moz-win-mediatext !important }

#FindToolbar > *,
.findbar-container > * {
  display: -moz-box !important }

statusbarpanel {
  -moz-appearance: statusbar;
  border: 0px !important }

#FindToolbar,
sidebarheader { 
  border: 0px !important }

/* Keep menus readable */
menulist > menupopup {
  -moz-appearance: menupopup !important }

menulist > menupopup > menuitem[_moz-menuactive="true"] {
  -moz-appearance: menuitem !important }

/* Toolbar Buttons Closer */
#toolbar-menubar > toolbarbutton,
#nav-bar > toolbarbutton,
#nav-bar > .toolbarbutton-icon,
#FindToolbar toolbarbutton,
#FindToolbar toolbarbutton *,
.bookmark-item .toolbarbutton-text {
  -moz-appearance:none !important;
  border: 0px !important;
  margin: 0px 0px 0px 1px !important;
  padding: 0px 0px 0px 0px !important }

#nav-bar > toolbarbutton * {
  border: 0px !important;
  margin: 0px 0px 0px 1px !important;
  padding: 0px  !important }

.chevron {
  -moz-appearance:none !important;
  margin: 0px 0px 0px 0px !important;
  padding: 0px 0px 0px 0px !important }

.bookmark-item {
  -moz-appearance:none !important;
  margin: 0px 0px 0px 0px !important;
  padding: 0px 0px 0px 0px !important }

.bookmark-item .toolbarbutton-icon {
  -moz-appearance:none !important;
  border: 0px !important;
  margin: 0px 0px 0px 2px !important;
  padding: 0px 0px 0px 0px !important }

/* Hide bookmarkmenubar, unhide by hovering Navigator Toolbox */
#PersonalToolbar {
  visibility: collapse !important }

#navigator-toolbox:hover > #PersonalToolbar {
  visibility: visible !important }

/* Transparent tooltip */
tooltip {
  -moz-appearance: none !important;
  background-color: transparent !important;
  color: -moz-win-mediatext !important;
  font-family: Calibri, Tahoma, sans-serif !important;
  background-image: url("data:image/png;base64,    iVBORw0KGgoAAAANSUhEUgAAABQAAAAUCAYAAACNiR0NAAAABGdBTUEAAK/INwWK6QAAABh0RVh0U29    mdHdhcmUAUGFpbnQuTkVUIHYzLjA4ZXKc4QAAAC9JREFUOE9j5LcMWsxATQAykJqYgZqGgX07aiDFET    QahpQn8tEwHA1DMsrKwZ9sADuo19oANiVcAAAAAElFTkSuQmCC") !important }

/* Translucent dialogs (too dark for vistaglazz toolbars) */
@-moz-document url(chrome://global/content/commonDialog.xul) {
  dialog {
    background: url("data:image/png;base64,      iVBORw0KGgoAAAANSUhEUgAAABQAAAAUCAYAAACNiR0NAAAABGdBTUEAAK/INwWK6QAAABh0RVh0U29      mdHdhcmUAUGFpbnQuTkVUIHYzLjA4ZXKc4QAAAC9JREFUOE9j5LcMWsxATQAykJqYgZqGgX07aiDFET      QahpQn8tEwHA1DMsrKwZ9sADuo19oANiVcAAAAAElFTkSuQmCC") !important }

  description, checkbox {
    color: -moz-win-mediatext !important;
    font-family: Calibri, Tahoma, sans-serif !important;
    font-size: 11pt !important }

  vbox > description {
    font-weight: bold !important }
}

/* TabMix: Tab Progressbar with system appearance */
tab .progress-bar {
  -moz-appearance: progresschunk !important }

#statusbar-progresspanel {
  display: none !important }

.searchbar-textbox{
   margin-right:0
}


#identity-box,
.autocomplete-history-dropmarker,
.textbox-input-box,
.search-go-container {
   margin-top: -1px !important;
   margin-bottom: -1px !important }

#identity-box > hbox,
#identity-box,
#page-proxy-stack,
.searchbar-engine-button {
   background: none;
   border-right: none!important }

#identity-box > hbox,
.searchbar-engine-button {
   padding-right: 1px !important }

#identity-box:hover > hbox,
.searchbar-engine-button:hover {
   padding-right: 0 !important;
   background-image: url(chrome://browser/skin/navbar-textbox-buttons.png);
   border-right: 1px solid ThreeDShadow!important }

#page-proxy-stack:hover {
   background-image: url(chrome://browser/skin/urlbar-favicon-glow.png) }

#page-proxy-stack {
   margin-left: -4px }
