css:
@-moz-document url-prefix("www.tumblr.com"), url-prefix("tumblr.com"), url-prefix("tumblr.com/dashboard"), url-prefix("tumblr"), domain("tumblr.com"), regexp("tumblr.com") {
#right_column .controls_section {
border-radius:0;
}
#right_column .controls_section li:last-child, #right_column .controls_section .last.after_showing {
border-bottom-right-radius: 0px;
border-bottom-left-radius: 0px;
}
#posts .post .post_question {
border-radius: 0px;
}
#right_column .controls_section li:first-child{
border-top-right-radius: 0px;
border-top-left-radius: 0px;
}
div.l-content #search_query {
border-radius: 0px;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
border-radius: 0px;
}
#posts .post .post_avatar {
border-radius: 0px;
}
.radar .radar_post {
border-radius: 0px;
}
.radar .radar_post .radar_content {
border-radius: 0px 0px 0 0
}
.photo_thumbnail {
border-radius: 0px;
}
.post .photo_post {
border-radius: 0px;
}
.radar .radar_content .thumbnail_anchor img {
border-radius: 0px;
}
.radar .radar_content .thumbnail_anchor {
border-radius: 0px;
}
.radar .radar_tumblelog_avatar {
border-radius: 0px;
}
#posts .post {
border-radius: 0px;
}
#return_to_top .return_to_top_icon {
background: -600px -190px url('http://static.tumblr.com/7n9siyl/Rekm5r5nt/png.png') no-repeat transparent;
}
img.content_top,img#content_top,img#content_bottom {
display: none;
}
.radar .radar_button.first {
border-radius: 0px 0px 0px 0px;
}
.radar .radar_button.last {
border-radius: 0px 0px 0px 0px;
}
#search_controls a.first {
border-bottom-right-radius: 0px;
border-bottom-left-radius: 0px;
}
#search_controls_wrapper #search_controls {
border-radius: 0px;
}
}

@-moz-document url-prefix("http://www.tumblr.com/new"), url-prefix('https://www.tumblr.com/new')   {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: #FFF;
}
#right_column {
margin: 10px 20px 0 0 !important;
}
.photo_post_box {
border-radius: 0px;
}
button {
border-radius: 0px !important;
}
input {
border-radius: 0px !important;
}
.embed_video_container {
border-radius: 0px;
}
ul.tabs li {
border-top-left-radius: 0px;
border-top-right-radius: 0px;
}
}
