/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document domain("iqdb.org") {
body {margin: 25px 8px 8px !important; padding: 8px !important;}
html,body {font-size: 16px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; width: auto !important; height: auto !important;} body>img[style*="moz-zoom-in"] {max-width: 100% !important; height: auto !important;}
html {background: white repeat-x !important; overflow-y: scroll !important; background-image: url("https://lh5.googleusercontent.com/-9mPfq_bTK4o/T4qBmWVF8eI/AAAAAAAAAMQ/ZojfswVTVrQ/I/backgrnd777.jpg"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAVCAYAAABsZT8HAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAANsAAADbABfWVZ+gAAAAd0SU1FB9oGAxEqHfMNphEAAAA5SURBVAjXY2SQUvnPwMDAwMLAyMQAYTAwQhmMDAwMDIzIUoyMOBkMmFIk6SJCMYxRH+rKwMDAwAAAGzcCz5PMGZsAAAAASUVORK5CYII=") !important; background-position: center 21px,0 0 !important;}

a {color: #034 !important;}

body>h1:first-of-type,body>h2:first-of-type {position: absolute !important; display: block !important; top: 0; width: auto !important; font-weight: bold !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; line-height: 21px !important; padding: 0 8px !important; margin: 0 !important; text-decoration: none !important; font-style: normal !important;}
body>h2:first-of-type {right: 0;}
body>h1:first-of-type {left: 0;}

input,textarea,select,button {-moz-appearance: none !important; font-family: sans-serif !important; color: black !important; font-size: 12px !important; border: 1px solid #ccc !important; background: #eee !important;}
input[type="checkbox"] {border: 1px solid #A6A697 !important; background-color: white !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}
input[type="file"] div {margin: 0 !important;}

form {margin: 0 !important;}
table.form {width: 50% !important; width: 615px !important; clear: left !important;}
table.form th {width: auto !important; padding: 0 !important; font-weight: bold !important;}
table.form th a { text-decoration: none !important;}
form>br,table.form~table.form tr:first-child+tr {display: none !important;}

table.form {border-spacing: 0 !important; border-collapse: collapse !important; font-size: 0.8em !important; border: 0 none !important; float: left !important;}
table.form:first-of-type {float: right !important; width: 500px !important;}
table.form:first-of-type tr th {border: 1px #A6A697  solid !important; background: none rgba(255,255,255,0.4) !important; vertical-align: middle !important; font-weight: normal !important;}
table.form:first-of-type tr:nth-child(odd) th {background-color: rgba(255,255,255,0.6) !important;}

ul {display: inline-block !important; width: auto !important;  clear: left !important;}
.flow {max-width: none !important; margin: 0 !important;}
.flow:first-child {display: none !important;}

.pages,.notice {font-size: 13px !important;}
.pages a {text-decoration: none !important;}
.pages a:hover {text-decoration: underline !important;}
.pages div,.notice {border: 1px solid #A6A697 !important; background: rgba(255,255,255,0.4) !important; margin: 2px 0 !important; padding: 2px 4px !important; border-radius: 5px !important;}
.pages table {border: 0 none !important; margin: 0 !important; width: 170px !important;}
.pages table th,#pages table td {padding: 0 !important; text-align: center !important;}
.notice {float: right !important; max-width: 600px !important; width: auto !important; word-wrap: break-word !important;}
}
