css:
@-moz-document domain('rootzwiki.com') {
#header_bar {position: static !important; border-bottom-right-radius: 0 !important; border-bottom-left-radius: 0 !important; border-top-left-radius: 5px !important; border-top-right-radius: 5px !important;}
#branding {min-height: 0px !important; padding-top: 0px !important;}
#logo, .headAd {display: none !important;}
#primary_nav {border-radius: 0 !important;}

div.articles br { line-height: 70% !important;}
#forum_table br {display: block !important;} 
table.ipb_table td {padding-top: 0 !important; padding-bottom: 0 !important;}
div.ipsBadge {margin-top: 5px !important;}
div.post_body {padding-top: 0px !important;}
div.post br {display: block !important; line-height: 70% !important;}
div.birdsayhi {display: block !important;}
div.author_info {padding-bottom: 0 !important;}
div.ipsLikeBar{margin-bottom: 80px !important;}
ul.post_controls {margin-bottom: 5px !important; clear: right !important;}
.signature {display: none !important;}
/*.post_ignore {display: none !important; } */

.twitter-share-button, #___plusone_0 {display: none !important;}
.__like {padding-top: 15px !important;}

ul.hfeed li br {display: block !important; line-height: 70% !important;}

#board_statistics p span.name { display: none !important;}

#content {width: 960px !important; max-width: 960px !important; min-width: 960px !important;}

#branding, #header_bar, #primary_nav {width: 980px !important; max-width: 980px !important; min-width: 980px !important;}

div#content div  { height: auto !important; }
div#content div h1 { margin-top: 0 !important; }

#user_navigation {float: left !important; padding-left: 230px !important;}
#primary_nav a { padding: 8px 12px !important;}
#search {padding-right: 175px !important;}
}
