css:
@-moz-document domain('123.popmundo.com'), domain('124.popmundo.com'), domain('125.popmundo.com'), domain('126.popmundo.com'), domain('www.popmundo.com')  {


body { background: #8fc4d2 url('http://images.poloscopie.fr/Game/Retro/WallPapers/tapet_club.gif') repeat !important; }

div#header { background-color: #8fc4d2; }

img[src="/App_Themes/PPM2/Images/Popmundo.png"]
{  
  width: 278px !important;  height: 0 !important;
  padding-top: 64px !important; 
  background-image: url(http://images.poloscopie.fr/Game/P2/App_Themes/PPM2/Images/Popomundo.png) !important; background-repeat: no-repeat !important;
}

table.data tr.even td {    background: #e2eff1; }
div.box table.data tr.even td {    background: #e2eff1; }

div#wrapper  { background: #8fc4d2; }


div.box { background: #a4d0df; }
div.box h2 { background: #e2eff1; color: #006699; }
div.box h2 a { color: #069 }
div.box div.box {background: #e2eff1;}
div.box div.box h2 {background: #8fc4d2; color: #006699; }

div.content h1 { color: #006699; }
div#sidemenu div.box h2 { background: #e2eff1; }

div.box table.data th { color: #069; background: #e2eff1; }

table.data th { background: #fff; color: #006699; }
table.sortable th.header { background-color: #fff; color: #006699; }
table.sortable th.header:hover { background-color: #b1cee0; }
table.sortable th.headerAsc { background-color: #b1cee0; }
table.sortable th.headerDesc { background-color: #b1cee0; }
table.data th a { color: #006699; }

div.box p.actionbuttons { border-top: 1px solid #82b0bd; }

div.box div.tabs { background: #e2eff1; }
div.box div.tabs a { color: #069; }
div.box div.tabs a:hover { text-decoration: underline; background: #39B; color:white }
div.box div.tabs a.active { color: black; background: #a4d0df; }

div.tb div.tbcg { background: #e2eff1; }

}
