css:
@-moz-document domain("tieba.baidu.com") {
/*---------------Information-------------------
            Author:网络孤独行客
            2012-06-20 01:34
----------------Information-------------------*/



/*---------------全局设置----------------------*/
a{
    font-family: 微软雅黑 !important;
}
.s_logo{
    display:none !important;
}
.th_abs {
    font-family: 微软雅黑 !important;
   
}
/*---------------背景-----------------------*/

.skin_201 .wrap2,.skin_201,.skin_1001 .wrap2,.skin_1001, .skin_1001_thum,
.skin_5 .wrap2,.skin_5,skin_5 .wrap1,.skin_5_thum,skin_106_thum,.skin_106,.skin_106 .wrap1,
.skin_106 .wrap2,skin_205_thum,.skin_205,
.skin_205 .wrap2{
    background:none !important;
}
/*---------------广告&新版帮助---------------*/
#aside_ad,.aside_ad_2,#quick_menu,.quick_menu{
    display:none !important;
}
/*----------------头部------------------------*/
#head{
    height:62px !important;
    padding:20px 0 !important;
}

/*----------------搜索栏----------------------*/
.search .s_nav {
    float: left !important;
    height: 30px !important;
    margin: 0 !important;
    padding: 0 !important;
    width: 400px !important;
}
.search .s_nav .s_tab {
    float: left !important;
    line-height: 30px !important;
    padding: 0 !important;
}

.search .s_ipt {
    height: 28px !important;
    background:none !important;
    width: 200px !important;
}

.search div {
    float: left !important;
    width: 450px !important;
}
.search .s_nav a {
    text-decoration: none !important;
    font-size:16px !important;
    color:#4F4F4F !important;
    
}
.search .s_nav a:hover{
    color:#ABCDEF !important;
}
.search .s_nav b {
    font-size:16px !important;
    color:#ABCDEF !important;  
}
.search .s_btn_wr {
    background:none !important;
}
.search .s_btn {
    background:none !important;
    height: 28px !important;
    background:#ABCDEF !important;
    color:#FFFFFF !important;
    line-height:28px;

}

/*----------------导航条-------------------------*/

.frs_nav_2, .frs_nav_2 .nav_wrap, .frs_nav_2 .nav_left, .frs_nav_2 .divide, .frs_nav_2 .icon_add_post, .frs_nav_2 a:hover, .frs_nav_2 .nav_icon {
    background-image: none !important;
    background-color:#009ad9 !important;
}
.frs_nav_2 .focus a:hover{
    background-color:#FFFFFF !important;
}
.frs_nav_2 .focus a{
    background-color:#FFFFFF !important;
}




ul.nav_left a:hover{background: none !important;}

ul.nav_left a,ul.nav_left li{background-image: none !important;}

/*----------------侧边栏------------------------*/

/*---------------用户信息-----------------------*/

#com_userbar a,.search a{
    color: #4F4F4F !important;
    text-decoration: none !important;
}
#com_userbar a:hover,.search a:hover{
    color: #ABCDEF !important;
    text-decoration: none !important;
}

/*--------------------链接---------------------*/
.aside a:link, .aside a:visited, .aside a:active, .aside a:hover {
    color: #FFFFFF !important;
}
.userlike_badge,.th_rp_num {
    background:none !important;
    
}
.userlike_member_title,.entrance_rank_index {
    color:#FFFFFF !important;
}
.userlike_member_lv {
    color:#FFFFFF !important;
    font-size:15px !important;
}

.userlike_info_expbar,.userlike_info_head {
    background:none !important;
    background-color:#FFFFFF !important;
}
.userlike_info_expbar_current {
    background:none !important;
    background-color:#ABCDEF !important;
}
.entrance_self_tc,.liked_forums_nav {
    background:none !important;
    }
.region_2{
    background-color:#0072BC !important;
}
.region_2 .region_title {
    color: #FFFFFF !important;
    font-size: 18px !important;

}
.region_2 .region_header {
    font: 12px/14px 微软雅黑 !important;
}
.region_2 .region_cnt {
    color: #FFFFFF !important;
    font: 12px/20px 微软雅黑 !important;

}
.member_num_o{
    color:#FFFFFF !important;
    }
.region_3 {
    clear: both;
    font-family: '微软雅黑' !important;

}

.region_3 .region_header {
    border-bottom:none !important;
    font-size: 18px !important;
    padding: 10px 0 8px !important;
}
.region_3 .region_title {
    color: #FFFFFF !important;
}

.zyq_2{
padding-top:10px !important;
}
.zyq_2 .mod .tl {
    border-bottom:none !important;
    padding: 10px 0 8px !important;
}
.zyq_2 .mod .tl a {

    color: #FFFFFF !important;
    font: bold 18px/14px '微软雅黑' !important;
}
.zyq_2 .zyq_mod_title, .zyq_2 .mod .tl a {
    color: #FFFFFF !important;
    font: bold 18px/14px '微软雅黑' !important;
}

.forum_info_2 h4 {
    color: #FFFFFF !important;
    }


/*-----------------相册----------------------------------*/
.alb_box, .alb_box_inner, .alb_box_bottom, .alb_box_bottom div {
    background: none !important;
}

.userlike_info_name{
    
    color: #FFFFFF !important;

}

.userlike_member_lv {

    background:none !important;
}
/*---------------贴吧信息--------------------------*/
#forumInfoPanel{
    background-color:#CD4900 !important;
    margin-top:10px !important;
}
.zyq_2 .mod{
    background-color:#008641;
}
/*---------------吧刊--------------------------------*/
.region_3 {
    margin-top:10px !important;
    background-color:#ABCDEF !important;
}

   
/*----------------游戏-------------------------------*/
.frs_gamezone_2 {
    background: none repeat scroll 0 0 #662D91 !important;
    margin-top: 10px !important;
}

/*----------------新版帮助--------------------------*/
.aside_button_mod {
    background-color:#ABCDEF !important;
    margin-top:10px;
    }


/*-----------------Main------------------------------*/
.th_list li {
    background-image: none !important;

}
#container, .frs_content {
    background-image:none !important;
}
}
