css:
@-moz-document domain('forums.somethingawful.com'), url-prefix('http://forums.somethingawful.com/') {

#content {
  margin: 20px auto 0 !important;
  max-width: 1120px !important;
}

#navigation, #nav_purchase, .navigation, .searchform {
  margin: 0 auto !important;
  max-width: 1120px !important;
}

ul#usercpnav {
  background: #E8E8E8 !important;
  border-color: #999 !important;
}

#usercpnav li {
  text-shadow: none !important;
}

#forum th, div.threadbar, div.forumbar, #forums th, #pm th, #info th, .standard h2, table.standard th {
  background: #006699 !important;
  background: -moz-linear-gradient(top, #006699 0%, #005588 100%) !important;
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#006699), color-stop(100%,#005588)) !important;
  background: -webkit-linear-gradient(top, #006699 0%,#005588 100%) !important;
}

div.standard.bookmarked_threads h2{
  background: #006699 !important;
}

td.postdate a, td.postlinks ul.profilelinks li a {
  border-radius: 2px !important;
  padding: 0 5px !important;
}

th a, h2 a {
  background: none !important;
  text-decoration: none !important;
}

a.forum, .subforums a { text-decoration: none !important; }

.bbc-spoiler.reveal, .bbc-spoiler.stay {
  background: none !important;
}

.bbc-spoiler {
  box-shadow: 0 0 0 #FFF !important;
}

div.oma_pal { display: none; !important }

#thread dl.userinfo dt.op {
  text-shadow: 1px 1px 0px #BBB !important;
}

div.pages a{
  width: 51px !important;
  box-shadow: none !important;
  color: #036 !important;
}

div.pages a:hover{
  width: 51px !important;
  box-shadow: none !important;
  color: #FFF !important;
}

div.pages span.disabled{
  box-shadow: none !important;
}

div.pages {
  padding-right: 7px !important;
  font-weight: bold !important;
}

div.pages select{
  font: 11px Segoe UI !important;
  font-weight: bold !important;
  height: 20px !important;
  border: 1px solid #C1C1C1 !important;
  color: #036 !important;
}

}
