css:
@-moz-document domain('leankitkanban.com'), domain("leankit.com") {
  .kb-tags {
    /* this hides the tag icon in corner or cards */
    background: none !important; 
  }

  .little-arrow, .little-arrow-background {
    /* hides the arrows that bring up the card context menu on hover */
    display: none !important;
  }

  body {
    /* removed the background image from all pages */
    background-image: none !important; 
    /* set the background color for all pages herd */
    background-color: #bbb !important;
  }

  .laneName {
    /* the text color of all lane names in lane headers */
    color: white !important;
  }

  .laneCapacity {
    /* the text color of the lane capacity number in the lane header */
    color: white !important;
  }

  .laneLaneHeader div {
    /* the color of half of the highest level lane headers */
    background-color: #005074 !important;
  }

  .parentLaneHeader div {
    /* the color of the other half of of the highest level lane headers */
    background-color: #005074 !important;
  }

  .allLaneHeader {
    /* the color of the inner lane headers */
    background-color: #2191c0 !important;
  }
}
