css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*Immunity Australis*/
dropmarker, .searchbar-dropmarker-image {
    -moz-appearance: none !important;
    background: url("chrome://global/skin/arrow/arrow-dn-sharp.gif") center center no-repeat  !important;
    -moz-image-region: rect(0 9px 5px 0) !important;
}


.searchbar-engine-image {
    margin-top: -2px !important;
    margin-bottom: -1px !important;
}

#navigator-toolbox[tabsontop="true"] #nav-bar {padding: 0px 0px !important;  max-height: 28px !important}

#urlbar {
    height: 24px !important;
    padding-top 0px !important;
    padding-bottom: 0px !important;
}

.autocomplete-history-dropmarker.urlbar-history-dropmarker{display:none !important}

#back-button {
    visibility: visible !important;
    position: relative !important;
    z-index: 200!important;
    margin-bottom: -10px !important;
    margin-top: -10px !important;
    margin-right: -15px !important;
    -moz-appearance: none!important;
    border: none!important;
    list-style-image: url(data:image/png;base64,) !important;
}

#back-button:hover{
    list-style-image: url(data:image/png;base64,%3D) !important;
}

#back-button:active {
    list-style-image: url(data:image/png;base64,%3D) !important;
}

#back-button[disabled="true"]{
    list-style-image: url(data:image/png;base64,) !important;
}

#forward-button {
    width: 35px!important;
    padding-left: 40px!important;
    list-style-image: url(data:image/png;base64,%3D)!important;
}

#forward-button[disabled="true"]{
    opacity: .35 !important; 
}

#star-button {
    visibility: visible !important;
}

#identity-box {
    visibility: visible !important;
    padding-left: 3px !important; 
    padding-top: 0px !important; 
    padding-bottom: 0px !important; 
}

#searchbar {
    height: 28px !important;
}

.tabbrowser-tab {
    font-weight:normal !important;
}

.tabbrowser-tab:not([pinned])[fadein]
{
    max-width: 200px !important;
    min-width: 100px !important;
}

#navigator-toolbox[tabsontop=true] #nav-bar {
    box-shadow: 0 1px 0 0 #d4cac0 inset,
         0 -1px 0 0 rgba(0,0,0,.05) !important;
    background-image: -moz-linear-gradient(#dad1c8,#f0ebe7) !important;
}

.tabbrowser-tab .tab-text {
    font-weight: normal !important;
}

.tabbrowser-tab[selected] .tab-text {
    font-weight: bold !important;
}


.browser { overflow-y: hidden !important; }

#navigator-toolbox > toolbar:not(#toolbar-menubar):not([autohide="true"]),
#navigator-toolbox > #toolbar-menubar > toolbarspring,
#navigator-toolbox > #toolbar-menubar:not([autohide="true"]) {
    -moz-binding: url("chrome://global/content/bindings/toolbar.xml#toolbar-drag") !important;
    background-color: #f0ebe7 !important;
}

tab .tab-close-button { -moz-box-ordinal-group: 0 !important; margin-right: 5px !important }
tab .tab-icon-image { -moz-box-ordinal-group: 1 !important; }
tab .tab-text { -moz-box-ordinal-group: 2 !important; }

.tabbrowser-tab[pending] .tab-content > * { opacity: .35 !important; }

tabs {
    margin-left: -10px !important;
}

#abp-toolbarbutton {
    padding: 0px !important;
    margin: 0px !important;
}

#abp-toolbarbutton dropmarker {
    display: none;
}



/*Australis Tab Styles*/
#TabsToolbar:not(:-moz-lwtheme) {
  background-repeat: repeat!important;
  background-position: center top!important;
  box-shadow: 0 -1px 0 hsla(209,67%,12%, .35) inset !important;
}

@media all and (-moz-windows-compositor) {
  #main-window[sizemode=normal] #TabsToolbar {
  box-shadow: none !important;
  }
}


#main-window[tabsontop="true"] .tabbrowser-tab:not([selected="true"]) .tab-text:not(:-moz-lwtheme) {
  color: inherit !important;
  text-shadow: inherit !important;
  }

.tab-stack {
  min-height: 28px !important;
}

.tabbrowser-tab {
  -moz-appearance: none!important;
  padding: 0!important;
  border: 0 solid transparent!important;
  background: transparent!important;
}

.tab-throbber[pinned],
.tab-icon-image[pinned] {
  -moz-margin-start: 0px!important;
  -moz-margin-end: 0px!important;
}

.tab-content {
  border: solid transparent!important;
  border-width: 0!important;
  padding: 4px 18px 4px!important;
  margin-left: -18px!important;
}
#tabbrowser-tabs > tab:first-child .tab-content {
  margin-left: -25px!important;
}

.tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox {
  -moz-padding-start: 25px!important;
  -moz-padding-end: 0px!important;
}

.tabbrowser-tab[selected="true"] > .tab-stack {
  z-index: 1!important;
  position: relative!important;
}

.tabbrowser-tab:-moz-lwtheme {
  color: inherit!important;
  text-shadow: inherit!important;
}

.tabs-newtab-button {
  -moz-margin-start: -18px !important;
  min-width: 50px !important;
  padding: 0!important;
  border: 0 solid transparent!important;
  background: transparent!important;
}

.tabbrowser-tab:not(:first-child) > .tab-stack > .tab-content:not([selected="true"]) {
  background-image: url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       25px,
                       right!important;
  background-size: 25px 30px,
                   -moz-calc(100% - 50px) 30px,
                   25px 30px!important;
  -moz-border-image: none!important;
}


.tabs-newtab-button:hover,
.tabbrowser-tab > .tab-stack > .tab-content:not([selected="true"]):hover {
  background-image: url("data:data:image/png;base64,"),
                    url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAAEnQAABJ0Ad5mH3gAAABbSURBVBhXPcvLCYAwEIThOWsFgi34Snxr1ELsv4GAhwQSMwp7+fhnYYFCZ8hLVcHa54b3/oJz7kQI4eMQDMsgxrj/pLmRVVhYszCxRjIIPX81pyLdT7q1rIbULyTOWk3IkRiVAAAAAElFTkSuQmCC"),
                    url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       25px,
                       right!important;
  background-size: 25px 30px,
                   -moz-calc(100% - 50px) 30px,
                   25px 30px!important;
  -moz-border-image: none!important;
}

.tabbrowser-tab:not([selected="true"]):hover + .tabbrowser-tab:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
  background-image: none!important;
}

.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not(:hover):not([selected="true"]) {
  background-image: url("data:image/png;base64,")!important;
  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       0px,
                       right!important;
  background-size: 50px 30px,
                   -moz-calc(100% - 0px) 30px,
                   0px 30px!important;
  -moz-border-image: none!important;
}

.tabbrowser-tab > .tab-stack > .tab-content[selected="true"] {
  position: relative!important;
   background-image: url("data:image/png;base64,"),
                    url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAYAAADtlXTHAAAABmJLR0QA1ADKAMCCarPAAAAACXBIWXMAABJ0AAASdAHeZh94AAAAB3RJTUUH3AYPCRANRanGYQAAAElJREFUCNdVjbERgDAQwxQ3FAzBEFTsvwEdHZNEFHzC0cmW7wywZN32g/s6DQ2CEpUgRQJp9qKyQ8CMQuRvv/HbSbAR6eNjEsADpwwxM3V8vdoAAAAASUVORK5CYII%3D"),
                    url("data:image/png;base64,")!important;

  background-repeat: no-repeat,
                     no-repeat,
                     no-repeat!important;
  background-position: left,
                       25px,
                       right!important;
  background-size: 25px 30px,
                   -moz-calc(100% - 50px) 30px,
                   25px 30px!important;
  -moz-border-image: none!important;
}

.tab-close-button {
  -moz-appearance: none!important;
  border: none!important;
  list-style-image: url("data:image/png;base64,%3D")!important;
}

.tab-close-button:hover,
.tab-close-button:hover[selected="true"] {
  -moz-appearance: none!important;
  border: none!important;
  list-style-image: url("data:image/png;base64,%3D%3D")!important;
}

.tab-close-button:hover:active,
.tab-close-button:hover:active[selected="true"] {
  -moz-appearance: none!important;
  border: none!important;
  list-style-image: url("data:image/png;base64,%3D")!important;
}
