css:
@-moz-document domain('sto-forum.perfectworld.com') {

/* ---- fix background on single-post view ---- */
body {
background-color: black !important;
}

/* ---- remove header and pull content up ---- */
.contentwrap {
background-image:none !important;
margin-top: -190px !important;
}

/* ---- make section headers in the index more distinctive ---- */
.txt-title-header div {
text-decoration: underline !important;
}

/* ---- Fixing Lists ---- */
ul {
list-style-type: disc !important;
margin-left:10px !important;
}
ul li ul {
list-style-type: square !important;
}
ul li ul li ul {
list-style-type: circle !important;
}
ol li {
margin-left:10px !important;
}


/* ---- GREY TEXT --- */
.time {
color: #C0C0C0 !important;
}
#posts {
color: #C0C0C0 !important;
}
.navbar {
color: #C0C0C0 !important;
}
.smallfont {
color: #C0C0C0 !important;
}
#threadslist {
color: #C0C0C0 !important;
}
.pagenav {
color: #C0C0C0 !important;
}
.tcat {
color: #C0C0C0 !important;
}
.thead {
color: #C0C0C0 !important;
}
.fieldset {
color: #C0C0C0 !important;
}
#collapseobj_threadreview {
color: #C0C0C0 !important;
}
#collapseobj_usercp_subthreads {
color: #C0C0C0 !important;
}
.PW-postbit-message {
color: #C0C0C0 !important;
}
.topic {
color: #C0C0C0 !important;
}

}
