Wiki_style_bkup_06_22


@-moz-document url-prefix("http://en.wikipedia.org/"), domain(wikipedia.org) {

/* Coded by Marcus Lee, 
   Orlando, FL, USA
   05/14/10 */

/* Globals */
body.mediawiki { background: #DADBDD !important; }

/* Header */
div#mw-head-base { background: #DADBDD !important; }
div#simpleSearch, form#searchform { margin-top: 0 !important; }
div#simpleSearch { position: absolute !important; top: 10px !important; right: 15px !important; }
#simpleSearch input#searchInput { width: 13em !important; }
div#p-namespaces, div#p-views, div#page-base, div#p-personal, div#siteNotice, div#p-cactions, div#protected-icon, div#spoken-icon, div#good-star { display: none !important; }

/* Left Nav */
div#panel, div#p-logo { background: #AAA !important; }
div#mw-panel { background: #AAA !important; position: absolute !important; top: 125px !important; }
div#p-logo a { background-position: 30% 0% !important; height: 115px !important; }
div#p-logo { height: 125px !important; top: -125px !important; }
div.portal { background: #CCC !important; margin: 0 6px 5px 6px !important; border: 1px solid #555 !important; }
div.portal a:visited { color: #0645AD !important; }

/* Main Content */
div#content { background: #DADBDD !important; float: right !important; position: absolute !important; top: 0px !important; }
table#toc, div#catlinks { background: #ECEEEE !important; }
div#content a:visited { color: #0645AD !important; }

/* Main Content - Section Headers */
h1#firstHeading {  margin: 10px 0 3px 0 !important; font-family: candara, serif !important; font-size: 16pt !important; font-weight: bold !important; }
div#bodyContent h2 { font-family: candara, serif !important; font-weight: bold !important; border-bottom: 1px solid #BBB !important; }

/* Main Content - Tables */
table.infobox, table.vertical-navboxtable.ambox, table.navbox, table.wikitable td { background: #ECEEEE !important; }
table.ambox { margin-bottom: 3px !important; }

/* Footer */
div#footer { display: none !important; }

/* en.wikipedia.org Homepage specific */
table#mp-topbanner tbody tr td table tbody tr td div { font-family: candara, serif !important; font-weight: bold !important; }
table#mp-topbanner { background: #DADDDD !important; margin-top: 20px !important; }
table#mp-upper td, table#mp-left, table#mp-right { background: #DADDDD !important; }
table#mp-upper td { border: none !important; }
}
