css:
@-moz-document url-prefix('http://www.tumblr.com'),url-prefix('http://www.tumblr.com/tagged/'),url('http://www.tumblr.com/following'),url('http://www.tumblr.com/likes'),url('http://www.tumblr.com/inbox'),url('http://www.tumblr.com/blog/queue'),url('http://www.tumblr.com/blog/drafts'),url('http://www.tumblr.com/blog/followers'),url('http://www.tumblr.com/blog/settings') , url-prefix("https://www.tumblr.com"), url-prefix("https://www.tumblr.com/tagged/"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/blog/queue"), url("https://www.tumblr.com/blog/drafts"), url("https://www.tumblr.com/blog/followers"), url("https://www.tumblr.com/blog/settings") {
* { text-shadow: none !important;}

body {
background: url(http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/01.jpg) bottom right no-repeat fixed !important;}

::-webkit-scrollbar-thumb:vertical {
background-color: #eaeaea;
height: 30px;
border-bottom: 5px solid #415d60;
border-top: 5px solid #415d60;}
 
::-webkit-scrollbar {
height: 30px;
width: 4px;
background-color: #d9d9d9 !important;}


#dashboard_controls_suggested_blogs {
background:#aaa !important;
border:none !important;}

#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta div.l-content, #dashboard_inbox div.l-content,#dashboard_tumblelog_settings div.l-content,#dashboard_followers #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(255,255,255,0.2) !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(255,255,255,0.2) !important;
}

#dashboard_switch_blog_menu_current,#dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current {
background-image:none !important;}


#footer,.dashboard_options_form_divider {
display:none;}



#left_column #dashboard_controls a, #left_column #dashboard_controls span {
color: #555 !important;}


#left_column #posts .notification {
background-color:#fff !important;
border-bottom:none !important;
color:#aaa !important;}



#left_column #posts .notification .avatar {
background-color:#fff !important;
border-color:transparent !important;
opacity:0.8 !important;}


#left_column #posts .notification .avatar:hover {
opacity:1 !important;}


#left_column #posts .notification .block,.post_info,.post_info a {
color:#aaa !important;}


#left_column #posts .notification a {
border:none !important;
color:#888 !important;}


#left_column #posts .notification blockquote {
border-left-color:#bbb !important;}

#left_column #posts .notification.alt,#dashboard_switch_blog_menu_footer,#dashboard_switch_blog_menu_wrapper {
background-color:#fff !important;}


#left_column #posts .post .post_avatar,#left_column #posts .post .sub_avatar .sub_avatar_inner {
background-color:#b1b1b1 !important;
border-bottom:none !important;}


#left_column #posts .post,.dashboard_options_form {
border-bottom:none !important;}

#nav .nav_item .nav_menu a {
border-top-color:#eee !important;
color:#888 !important;}




#nav .nav_item .nav_menu,#dashboard_switch_blog_menu_header {
background-color:#f7f7f7 !important;
border:none !important;}

#nav .nav_item.active,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel,#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background:#6e6e6e !important;}

#pagination a {
background: #888 !important;
color: #eee !important;}



#pagination a:hover {
background: #666 !important;}



#posts .post .footer_links a,#posts .post .post_controls a {
color:#bbb !important;}

#right_column .dashboard_nav_item {
background:none !important;
border-bottom:1px solid #aaa !important;}


#show_radar,#hide_radar,#hide_recommended_blogs,#show_recommended_blogs {
background:none !important;}

.radar {display: none;}

#auto_pagination_loader {
background: transparent!important;
border: 0px!important;
font-weight: normal;
font-family: helvetica;
font-style: normal;
color: #1c1c1c;}


.dashboard_controls_radar_media {
background:#aaa !important;}

.dashboard_controls_suggested_blog {
border:1px solid #999 !important;
border-width:0 0 1px !important;}



.dashboard_options_form, .dashboard_options_form_disclaimer {
background:#fff !important;
color: #555 !important;}


.dashboard_switch_blog_menu_item {
background-color:#f7f7f7 !important;
border:0 !important;}


.nav_item:hover {
color:#eee !important;}

.new_post_label {
-webkit-text-stroke:0 !important;
color:rgba(0,0,0,0) !important;
opacity:1 !important;
top: 0px !important;
padding-top: 80px;}

.new_post_label:hover {
-webkit-text-stroke:0 !important;
color:rgba(0,0,0,0) !important;
opacity:1 !important;
top: 0px !important;
padding-top: 80px;}


.notification_type_icon {
display:none !important;}

.sub_avatar {
background-color:transparent !important;
height:32px !important;
width:32px !important;}

h1,h2,h3,h4,h5,h6 {
color:#666 !important;}


ol#posts li.post.new_post:hover {
background-position: center bottom !important;}


.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important;}

.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 2px 2px 2px rgba(0,0,0, 0.50) !important;
  border-radius: 12px !important;}



.content.is_media, .mask.top, .mask.bottom {
  background: none !important;}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255, .20) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(255,255,255,0.20)  !important;}


.post.conversation.same_user_as_last.is_mine:hover,.post.conversation.same_user_as_last.is_reblog.is_mine:hover,.post.conversation.not_mine:hover,.post.conversation.is_mine:hover,.post.conversation.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.note.same_user_as_last.is_mine:hover,.post.note.same_user_as_last.is_reblog.is_mine:hover,.post.note.not_mine:hover,.post.note.is_mine:hover,.post.note.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.regular.same_user_as_last.is_mine:hover,.post.regular.same_user_as_last.is_reblog.is_mine:hover,.post.regular.not_mine:hover,.post.regular.is_mine:hover,.post.regular.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.link.same_user_as_last.is_mine:hover,.post.link.same_user_as_last.is_reblog.is_mine:hover,.post.link.not_mine:hover,.post.link.is_mine:hover,.post.link.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.quote.same_user_as_last.is_mine:hover,.post.quote.same_user_as_last.is_reblog.is_mine:hover,.post.quote.not_mine:hover,.post.quote.is_mine:hover,.post.quote.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.audio.same_user_as_last.is_mine:hover,.post.audio.same_user_as_last.is_reblog.is_mine:hover,.post.audio.not_mine:hover,.post.audio.is_mine:hover,.post.audio.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.video.same_user_as_last.is_mine:hover,.post.video.same_user_as_last.is_reblog.is_mine:hover,.post.video.not_mine:hover,.post.video.is_mine:hover,.post.video.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.post.photo.same_user_as_last.is_mine:hover,.post.photo.same_user_as_last.is_reblog.is_mine:hover,.post.photo.not_mine:hover,.post.photo.is_mine:hover,.post.photo.is_reblog.is_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;}


.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(177,180,187, .85) !important;
  border-bottom-left-radius: 18px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;}

.permalink:hover {background: rgba(143,153,166, 1.0 ) !important;}

.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {
   border-bottom-left-radius: 6px !important;
   border-bottom-right-radius: 6px !important;}


.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .following, .following, .selected .posts, #left_column DIV:nth-child(9) {
   border-top-left-radius: 6px !important; 
   border-top-right-radius: 6px !important;}


.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255, 1.0) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;}



.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(205,123,184, .50) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 1px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;}

#posts .notification.first_notification .nipple.border, #posts .notification_alt .nipple.border, #posts .notification .nipple.border {
  border: none !important;}

#posts .avatar_frame, .notification.single_notification {
  border: 1px rgba(205,123,184, 1.0) solid !important;}


#right_column .controls_section li .tag, #right_column .controls_section li .likes, #right_column .controls_section li .following, #right_column .controls_section li .posts, #right_column .controls_section li .followers, #right_column .controls_section li .messages, #right_column .controls_section li .drafts, #right_column .controls_section li .queue, #right_column .controls_section li .settings {color:#f1f1f1 !important;}

#right_column .controls_section li .count {color:#84a1a4!important;}

 
li.selected, li.recessed, a.active, div.post_filter_tabs a {background: none !important;}


ol#posts li.notification {
  border: 1px solid rgba(0,0,0,0.0) !important;
  color: #fff !important;}

#posts .notification .avatar_frame {
background: rgba(0,0,0,0.0) !important;}

.post.is_mine.with_avatar.new_post {
  color: #ffffff !important;
  background: rgba(255,255,255,0.0) !important;}

#new_post .arrow {
background: none!important;
width: 0px!important;
height: 0px!important;
border-right: solid 9px rgba(255,255,255, .10)!important;
border-bottom: 9px solid transparent!important;
border-left: 2px solid transparent!important;
border-top: 9px solid transparent!important;
margin-top: 0!important;}

.new_post_label .new_post_label_icon {
background-image: url('http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/icons.png') !important;
background-repeat: no-repeat;
position: absolute;
display: block;
background-size: auto !important;
top: 5px !important;
left: 0px !important;
opacity:.9 !important;}

.new_post_label:hover .new_post_label_icon:hover{
opacity: 1.0 !important;}

#return_to_top .return_to_top_icon{
background: url('http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/top.png') no-repeat transparent!important;}

#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video{opacity:.9 !important;}

#new_post_label_text:hover,#new_post_label_photo:hover,#new_post_label_quote:hover,#new_post_label_link:hover,#new_post_label_chat:hover,#new_post_label_audio:hover,#new_post_label_video:hover{opacity: 1.0 !important;}

#logo {
height: 0 !important;
width: 0 !important;
padding-left: 300px !important;
padding-top: 70px !important;
background: url(http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/logo.png) no-repeat !important;}

div#right_column, div#right_column:after {
  background: none !important;}

#sidebar_footer_nav.pinned_sidebar_footer {
display:none !important; visibility: hidden !important;}


.photoset {
background:#fff !important;}


.photo_reply, *:hover photo_reply {
  background-color: #FFFFFF !important;
  opacity: 1 !important;
  background-position: 0 0 !important;}

.photo_reply {
  background-image: url("http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/c.png") !important;}

*:hover .photo_reply {
  background-image: url("http://img.photobucket.com/albums/v246/miyaviusa/choomtopthemes/actors/chover.png") !important;}

.tab.iconic,.private_label{
background:rgba(255,255,255,.30)!important;
height:10px!important;
border-radius:20px!important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(255,255,255,0.2) !important;}

div.l-content .tabs_3 {
background:rgba(255,255,255,0.2) !important;}

div.l-content #tabs .tab.selected {
background:rgba(255,255,255,0.2) !important;
border:0px;width:250px;}

div.l-content #tabs .tab {
background:none !important;
border:0px;
width:160px;}

div.l-content #tabs .tab.left_of_selected {
background:none !important;
border:0px;
width:160px;}

#content_bottom.content_cap {
opacity: .20 !important;}



div.l-content #left_column .dashboard_options_form {
background:rgba(255,255,255,0.0) !important;
border-color:transparent !important;
border:0px !important;
}

.dashboard_options_form_disclaimer  {
background:rgba(255,255,255,0.0) !important;
border-color:transparent !important;
border:0px !important;
}

div.l-content #left_column .no_posts_found {
color: #fff !important;
}


div.l-content #left_column .avatar_and_i .post_avatar.flat.lighter_blue {
background:rgba(255,255,255,0.0) !important;
border-color:transparent !important;
border:0px !important;
}

.publish_on_day {
color: #fff !important;
}
.publish_on_time {
color: #fff !important;
}

div.l-content #left_column .white_pane {
background:rgba(255,255,255,0.0) !important;
border-color:transparent !important;
border:0px !important;
}




#content_top.content_cap {opacity: .20;}
}
