css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("friendfeed.com") {


a
{
text-decoration:none;
}
a:hover
{
text-decoration:underline;
}
div
{
font-family:tahoma;
font-size:9pt;
line-height:1.7;
}
#body
{
direction:rtl;
}
#settingspromo
{
position:absolute;
left:0px;
top:20px;
}
#rightsearch
{
position:absolute;
left:45px;
top:0px;
}
.friend
{
text-decoration:none;
}
a.friend:hover
{
color:black;
border-bottom-color:#000000;
}
.summary
{
border:solid 1px #93BAEB;
border-bottom-width:3px;
padding:5px;
background-color:#E8F2FF;
direction:ltr;
}
.entries
{
border:solid 1px #E8F2FF;
border-bottom-width:6px;
padding:5px;
background-color:#F9FAFB;
overflow-x:hidden;
}
.info
{
margin-top:5px;
direction:ltr;
border-left:solid 2px #808080;
padding-left:4px;
}
.likes
{
direction:ltr;
}
.pager
{
/*position:fixed;
left:5px;
bottom:5px;*/
border:solid 1px #D1D1D1;
padding:2px;
background-color:#F3F3F3;
text-align:center;
margin:0px;
padding:4px;
}
#feedviews
{
text-align:center;
direction:ltr;
margin:0px;
padding:0px;
}
#footer
{
margin:0px;
text-align:center;
}

.comment
{
direction:rtl;
border-right:solid 4px #DCDCDC;

padding-right:4px;
}
.toggle
{
direction:ltr;
}
#infobox
{
position:absolute;
left:0px;
top:0px;
}
.friendname
{
direction:ltr;
padding-bottom:2px;
padding-top:2px;
margin-bottom:0px;
border-bottom:dotted 1px gray;
}
div.friendname:hover
{
background-color:#DFEAF8;
}
.section
{
direction:ltr;
border-top:solid 2px #6797D3;
padding-top:2px;
}
#sharedialog
{
direction:ltr;
}
}
