css:
@namespace url(http://www.w3.org/1999/xhtml);

/*  rewritten from original at:  http://userstyles.org/styles/58317/youtube-bigger-hd-player-for-hd-videos  */

@-moz-document domain("youtube.com") {
    /*assorted fixes*/ 
    #watch-main-container{position:relative; top: 12mm !important;}
    #watch-video-container{background-color: black !important; background-image: none !important;}
    #watch-sidebar{margin-top: 2ex !important; outline: silver 1px solid !important;} 
    #watch-sidebar{box-shadow: inset 8ex 0ex 16ex -2ex silver !important;}
    
    /*resize flash video player containers*/
    #watch-video {
        width: 100% !important;
        height:  720px !important;
        margin: auto !important;
        padding-top: 65px !important;
        padding-bottom: 65px !important; 
        background-color: black !important;
        z-index: 2000 !important;          
        top: 12mm !important;

    }
    #watch-video #watch-player {
        width: 98% !important;
        height: 95% !important;
        margin: auto !important;
        box-shadow: 0px -4px 16px -6px #900000 !important;

    }

}
